void Player::SendActionButtons(uint32 state) const
{
    WorldPacket data(SMSG_ACTION_BUTTONS, 1+(MAX_ACTION_BUTTONS*4));
    /*
        state can be 0, 1, 2
        0 - Sends initial action buttons, client does not validate if we have the spell or not
        1 - Used used after spec swaps, client validates if a spell is known.
        2 - Clears the action bars client sided. This is sent during spec swap before unlearning and before sending the new buttons
    */
    ObjectGuid buttons[MAX_ACTION_BUTTONS];
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
    {
        ActionButtonList::const_iterator itr = m_actionButtons.find(i);
        if (itr != m_actionButtons.end() && itr->second.uState != ACTIONBUTTON_DELETED)
            buttons[i] = itr->second.packedData;
        else
            buttons[i] = 0;
    }

    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][0]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][1]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][3]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][4]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][6]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][7]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][2]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteBit(buttons[i][5]);

    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][5]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][2]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][0]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][1]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][3]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][6]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][4]);
    for (uint8 i = 0; i < MAX_ACTION_BUTTONS; ++i)
        data.WriteByteSeq(buttons[i][7]);

    data << uint8(state);
    GetSession()->SendPacket(&data);

    TC_LOG_DEBUG("network", "Action Buttons for '%u' spec '%u' Sent", GetGUIDLow(), GetActiveSpec());
}

bool Player::IsActionButtonDataValid(uint8 button, uint32 action, uint8 type)
{
    if (button >= MAX_ACTION_BUTTONS)
    {
        TC_LOG_ERROR("player.loading", "Action %u not added into button %u for player %s: button must be < %u", action, button, GetName(), MAX_ACTION_BUTTONS);
        return false;
    }

    /*if (action >= MAX_ACTION_BUTTON_ACTION_VALUE)
    {
        TC_LOG_ERROR("player.loading", "Action %u not added into button %u for player %s: action must be < %u", action, button, GetName(), MAX_ACTION_BUTTON_ACTION_VALUE);
        return false;
    }*/

    switch (type)
    {
        case ACTION_BUTTON_SPELL:
            if (!sSpellMgr->GetSpellInfo(action))
            {
                TC_LOG_ERROR("player.loading", "Spell action %u not added into button %u for player %s: spell not exist", action, button, GetName());
                return false;
            }

            if (!HasSpell(action))
            {
                TC_LOG_DEBUG("player.loading", "Player::IsActionButtonDataValid Spell action %u not added into button %u for player %s: player don't known this spell", action, button, GetName());
                return false;
            }
            break;
        case ACTION_BUTTON_ITEM:
            if (!sObjectMgr->GetItemTemplate(action))
            {
                TC_LOG_ERROR("player.loading", "Item action %u not added into button %u for player %s: item not exist", action, button, GetName());
                return false;
            }
            break;
        default:
            break;                                          // other cases not checked at this moment
    }

    return true;
}

ActionButton* Player::addActionButton(uint8 button, uint32 action, uint8 type)
{
    if (!IsActionButtonDataValid(button, action, type))
        return NULL;

    // it create new button (NEW state) if need or return existed
    ActionButton& ab = m_actionButtons[button];

    // set data and update to CHANGED if not NEW
    ab.SetActionAndType(action, ActionButtonType(type));

    TC_LOG_DEBUG("player.loading", "Player '%u' Added Action '%u' (type %u) to Button '%u'", GetGUIDLow(), action, type, button);
    return &ab;
}

void Player::removeActionButton(uint8 button)
{
    ActionButtonList::iterator buttonItr = m_actionButtons.find(button);
    if (buttonItr == m_actionButtons.end() || buttonItr->second.uState == ACTIONBUTTON_DELETED)
        return;

    if (buttonItr->second.uState == ACTIONBUTTON_NEW)
        m_actionButtons.erase(buttonItr);                   // new and not saved
    else
        buttonItr->second.uState = ACTIONBUTTON_DELETED;    // saved, will deleted at next save

    TC_LOG_DEBUG("player.loading", "Action Button '%u' Removed from Player '%u'", button, GetGUIDLow());
}

ActionButton const* Player::GetActionButton(uint8 button)
{
    ActionButtonList::iterator buttonItr = m_actionButtons.find(button);
    if (buttonItr == m_actionButtons.end() || buttonItr->second.uState == ACTIONBUTTON_DELETED)
        return NULL;

    return &buttonItr->second;
}

int8 Player::GetFreeActionButton()
{
    // 12 is max button of first action bar
    for (uint8 i = 0; i < 12; i++)
        if (!GetActionButton(i))
            return i;

    return -1;
}

bool Player::UpdatePosition(float x, float y, float z, float orientation, bool teleport, bool stop /*=false*/)
{
    // half opt method
    int gx=(int)(32-x/SIZE_OF_GRIDS);                       //grid x
    int gy=(int)(32-y/SIZE_OF_GRIDS);                       //grid y

    if (gx >= MAX_NUMBER_OF_GRIDS || gy >= MAX_NUMBER_OF_GRIDS)
    {
        //Ban
        // std::ostringstream ss;
        // ss << "Out of Map " << GetMapId() << " x:" << x << " y:" << y  << " z:" << z;
        // sWorld->BanAccount(BAN_CHARACTER,GetName(),"45d", ss.str().c_str(), "System");
        return false;
    }

    if (!Unit::UpdatePosition(x, y, z, orientation, teleport))
        return false;

    //if (movementInfo.flags & MOVEMENTFLAG_MOVING)
    //    mover->RemoveAurasWithInterruptFlags(AURA_INTERRUPT_FLAG_MOVE);
    //if (movementInfo.flags & MOVEMENTFLAG_TURNING)
    //    mover->RemoveAurasWithInterruptFlags(AURA_INTERRUPT_FLAG_TURNING);
    //AURA_INTERRUPT_FLAG_JUMP not sure

    // group update
    if (GetGroup())
        SetGroupUpdateFlag(GROUP_UPDATE_FLAG_POSITION);

    if (GetTrader() && !IsWithinDistInMap(GetTrader(), INTERACTION_DISTANCE))
        GetSession()->SendCancelTrade();

    CheckAreaExploreAndOutdoor();

    // Enable check for zone.
    if (!m_zoneUpdateAllow && !m_lastZoneUpdPos.IsInDist(this, sWorld->GetVisibilityRelocationLowerLimit()))
    {
        m_zoneUpdateAllow = true;
        m_lastZoneUpdPos = *this;
    }
    return true;
}

