void Player::CheckAreaExploreAndOutdoor()
{
    if (!IsAlive())
        return;

    if (isInFlight())
        return;

    bool isOutdoor;
    uint32 areaId = GetBaseMap()->GetAreaId(GetPositionX(), GetPositionY(), GetPositionZ(), &isOutdoor);
    AreaTableEntry const* areaEntry = sAreaTableStore.LookupEntry(areaId);

    if (sWorld->getBoolConfig(CONFIG_VMAP_INDOOR_CHECK) && !isOutdoor)
        RemoveAurasWithAttribute(SPELL_ATTR0_OUTDOORS_ONLY);

    if (!areaId)
        return;

    if (!areaEntry)
    {
        TC_LOG_ERROR("server", "Player '%s' (%u) discovered unknown area (x: %f y: %f z: %f map: %u areaId %u)",
            GetName(), GetGUIDLow(), GetPositionX(), GetPositionY(), GetPositionZ(), GetMapId(), areaId);
        return;
    }

    uint32 offset = areaEntry->exploreFlag / 32;

    if (offset >= PLAYER_EXPLORED_ZONES_SIZE)
    {
        TC_LOG_ERROR("entities.player", "Player::CheckAreaExploreAndOutdoor: Wrong area flag %u in map data for (X: %f Y: %f) point to field PLAYER_EXPLORED_ZONES_1 + %u ( %u must be < %u ).",
            areaEntry->exploreFlag, GetPositionX(), GetPositionY(), offset, offset, PLAYER_EXPLORED_ZONES_SIZE);
        return;
    }

    uint32 val = (uint32)(1 << (areaEntry->exploreFlag % 32));
    uint32 currFields = GetUInt32Value(PLAYER_EXPLORED_ZONES_1 + offset);

    if (!(currFields & val))
    {
        SetUInt32Value(PLAYER_EXPLORED_ZONES_1 + offset, (uint32)(currFields | val));

        UpdateAchievementCriteria(CRITERIA_TYPE_EXPLORE_AREA);

        if (areaEntry->area_level > 0)
        {
            uint32 area = areaEntry->ID;
            if (getLevel() >= sWorld->getIntConfig(CONFIG_MAX_PLAYER_LEVEL))
            {
                SendExplorationExperience(area, 0);
            }
            else
            {
                int32 diff = int32(getLevel()) - areaEntry->area_level;
                uint32 XP = 0;

                float ExploreXpRate = 1;
                if(GetPersonnalXpRate())
                   ExploreXpRate = GetPersonnalXpRate();
                else
                   ExploreXpRate = sWorld->getRate(RATE_XP_EXPLORE);

                if (diff < -5)
                {
                    XP = uint32(sObjectMgr->GetBaseXP(getLevel()+5) * ExploreXpRate);
                }
                else if (diff > 5)
                {
                    int32 exploration_percent = (100-((diff-5)*5));
                    if (exploration_percent > 100)
                        exploration_percent = 100;
                    else if (exploration_percent < 0)
                        exploration_percent = 0;

                    XP = uint32(sObjectMgr->GetBaseXP(areaEntry->area_level) * exploration_percent / 100 * ExploreXpRate);
                }
                else
                {
                    XP = uint32(sObjectMgr->GetBaseXP(areaEntry->area_level) * ExploreXpRate);
                }

                GiveXP(XP, NULL);
                SendExplorationExperience(area, XP);
            }
            TC_LOG_DEBUG("player", "Player %u discovered a new area: %u", GetGUIDLow(), area);
        }
    }
}

uint32 Player::TeamForRace(uint8 race)
{
    if (ChrRacesEntry const* rEntry = sChrRacesStore.LookupEntry(race))
    {
        switch (rEntry->TeamID)
        {
            case 1: return HORDE;
            case 7: return ALLIANCE;
            case 42: return PANDAREN_NEUTRAL;
        }
        TC_LOG_ERROR("player", "Race (%u) has wrong teamid (%u) in DBC: wrong DBC files?", uint32(race), rEntry->TeamID);
    }
    else
        TC_LOG_ERROR("player", "Race (%u) not found in DBC: wrong DBC files?", uint32(race));

    return ALLIANCE;
}

void Player::setFactionForRace(uint8 race)
{
    m_team = TeamForRace(race);

    ChrRacesEntry const* rEntry = sChrRacesStore.LookupEntry(race);
    setFaction(rEntry ? rEntry->FactionID : 0);
}

ReputationRank Player::GetReputationRank(uint32 faction) const
{
    FactionEntry const* factionEntry = sFactionStore.LookupEntry(faction);
    return GetReputationMgr().GetRank(factionEntry);
}

//Calculate total reputation percent player gain with quest/creature level
int32 Player::CalculateReputationGain(uint32 creatureOrQuestLevel, int32 rep, int32 faction, bool for_quest, bool noQuestBonus)
{
    float percent = 100.0f;

    // Get the generic rate first
    if (RepRewardRate const* repData = sObjectMgr->GetRepRewardRate(faction))
    {
        float repRate = for_quest ? repData->quest_rate : repData->creature_rate;
        percent *= repRate;
    }

    float rate = for_quest ? sWorld->getRate(RATE_REPUTATION_LOWLEVEL_QUEST) : sWorld->getRate(RATE_REPUTATION_LOWLEVEL_KILL);

    if (rate != 1.0f && creatureOrQuestLevel <= Trinity::XP::GetGrayLevel(getLevel()))
        percent *= rate;

    float repMod = noQuestBonus ? 0.0f : (float)GetTotalAuraModifier(SPELL_AURA_MOD_REPUTATION_GAIN);

    if (!for_quest)
        repMod += GetTotalAuraModifierByMiscValue(SPELL_AURA_MOD_FACTION_REPUTATION_GAIN, faction);

    percent += rep > 0 ? repMod : -repMod;

    if (percent <= 0.0f)
        return 0;

    return int32(rep*percent/100);
}

//Calculates how many reputation points player gains in victim's enemy factions
void Player::RewardReputation(Unit* victim, float rate)
{
    if (!victim || victim->GetTypeId() == TYPEID_PLAYER)
        return;

    if (victim->ToCreature()->IsReputationGainDisabled())
        return;

    ReputationOnKillEntry const* Rep = sObjectMgr->GetReputationOnKilEntry(victim->ToCreature()->GetCreatureTemplate()->Entry);

    if (!Rep)
        return;

    uint32 ChampioningFaction = 0;

    if (GetChampioningFaction())
    {
        // support for: Championing - http://www.wowwiki.com/Championing

        Map const* map = GetMap();
        if (map && map->IsDungeon())
        {
            uint32 dungeonLevel = GetChampioningFactionDungeonLevel();
            if (dungeonLevel)
            {
                InstanceTemplate const *instance = sObjectMgr->GetInstanceTemplate(map->GetId());
                if (instance)
                {
                    AccessRequirement const *pAccessRequirement = sObjectMgr->GetAccessRequirement(map->GetId(), ((InstanceMap*)map)->GetDifficulty());
                    if (pAccessRequirement)
                    {
                        if (!map->IsRaid() && pAccessRequirement->levelMin >= dungeonLevel)
                            ChampioningFaction = GetChampioningFaction();
                    }
                }
            }
            else
                ChampioningFaction = GetChampioningFaction();
        }
    }

    // Favored reputation increase START
    uint32 team = GetTeam();
    float favored_rep_mult = 0;

    if ((HasAura(32096) || HasAura(32098)) && (m_zoneUpdateId == 3483 || m_zoneUpdateId == 3562 || m_zoneUpdateId == 3836 || m_zoneUpdateId == 3713 || m_zoneUpdateId == 3714)) favored_rep_mult = 0.25; // Thrallmar's Favor and Honor Hold's Favor
    else if (HasAura(30754) && (Rep->RepFaction1 == 609 || Rep->RepFaction2 == 609) && !ChampioningFaction)                   favored_rep_mult = 0.25; // Cenarion Favor

    if (favored_rep_mult > 0) favored_rep_mult *= 2; // Multiplied by 2 because the reputation is divided by 2 for some reason (See "donerep1 / 2" and "donerep2 / 2") -- if you know why this is done, please update/explain :)
    // Favored reputation increase END

    float recruitAFriend = GetsAFriendBonus(false);

    if (Rep->RepFaction1 && (!Rep->TeamDependent || team == ALLIANCE))
    {
        int32 donerep1 = CalculateReputationGain(victim->getLevel(), Rep->RepValue1, ChampioningFaction ? ChampioningFaction : Rep->RepFaction1, false);
        donerep1 = int32(donerep1*(rate + favored_rep_mult));

        if (recruitAFriend)
            donerep1 = int32(donerep1 * (1 + sWorld->getRate(RATE_REPUTATION_RECRUIT_A_FRIEND_BONUS)));

        FactionEntry const* factionEntry1 = sFactionStore.LookupEntry(ChampioningFaction ? ChampioningFaction : Rep->RepFaction1);
        uint32 current_reputation_rank1 = GetReputationMgr().GetRank(factionEntry1);
        if (factionEntry1 && current_reputation_rank1 <= Rep->ReputationMaxCap1)
            GetReputationMgr().ModifyReputation(factionEntry1, donerep1);
    }

    if (Rep->RepFaction2 && (!Rep->TeamDependent || team == HORDE))
    {
        int32 donerep2 = CalculateReputationGain(victim->getLevel(), Rep->RepValue2, ChampioningFaction ? ChampioningFaction : Rep->RepFaction2, false);
        donerep2 = int32(donerep2*(rate + favored_rep_mult));

        if (recruitAFriend)
            donerep2 = int32(donerep2 * (1 + sWorld->getRate(RATE_REPUTATION_RECRUIT_A_FRIEND_BONUS)));

        FactionEntry const* factionEntry2 = sFactionStore.LookupEntry(ChampioningFaction ? ChampioningFaction : Rep->RepFaction2);
        uint32 current_reputation_rank2 = GetReputationMgr().GetRank(factionEntry2);
        if (factionEntry2 && current_reputation_rank2 <= Rep->ReputationMaxCap2)
            GetReputationMgr().ModifyReputation(factionEntry2, donerep2);
    }
    
    // Implementation for cataclysm with empty default faction
    if (!Rep->RepFaction1 && !Rep->RepFaction2 && ChampioningFaction)
    {
        int32 donerep = CalculateReputationGain(victim->getLevel(), Rep->RepValue1, ChampioningFaction, false);
        donerep = int32(donerep*(rate + favored_rep_mult));

        if (recruitAFriend)
            donerep = int32(donerep * (1 + sWorld->getRate(RATE_REPUTATION_RECRUIT_A_FRIEND_BONUS)));

        FactionEntry const *factionEntry = sFactionStore.LookupEntry(ChampioningFaction);
        uint32 current_reputation_rank = GetReputationMgr().GetRank(factionEntry);
        if (factionEntry && current_reputation_rank <= Rep->ReputationMaxCap1)
            GetReputationMgr().ModifyReputation(factionEntry, donerep);
    }
}

//Calculate how many reputation points player gain with the quest
void Player::RewardReputation(Quest const* quest)
{
    float recruitAFriend = GetsAFriendBonus(false);

    // quest reputation reward/loss
    for (uint8 i = 0; i < QUEST_REPUTATIONS_COUNT; ++i)
    {
        if (!quest->RewardFactionId[i])
            continue;
        if (quest->RewardFactionValueIdOverride[i])
        {
            int32 rep = CalculateReputationGain(GetQuestLevel(quest), quest->RewardFactionValueIdOverride[i]/100, quest->RewardFactionId[i], true, true);

            if (recruitAFriend)
                rep = int32(rep * (1 + sWorld->getRate(RATE_REPUTATION_RECRUIT_A_FRIEND_BONUS)));

            if (FactionEntry const* factionEntry = sFactionStore.LookupEntry(quest->RewardFactionId[i]))
                GetReputationMgr().ModifyReputation(factionEntry, rep);
        }
        else
        {
            uint32 row = ((quest->RewardFactionValueId[i] < 0) ? 1 : 0) + 1;
            uint32 field = abs(quest->RewardFactionValueId[i]);

            if (const QuestFactionRewEntry* pRow = sQuestFactionRewardStore.LookupEntry(row))
            {
                int32 repPoints = pRow->QuestRewFactionValue[field];

                if (!repPoints)
                    continue;

                repPoints = CalculateReputationGain(GetQuestLevel(quest), repPoints, quest->RewardFactionId[i], true);

                if (recruitAFriend)
                    repPoints = int32(repPoints * (1 + sWorld->getRate(RATE_REPUTATION_RECRUIT_A_FRIEND_BONUS)));

                if (const FactionEntry* factionEntry = sFactionStore.LookupEntry(quest->RewardFactionId[i]))
                    GetReputationMgr().ModifyReputation(factionEntry, repPoints);
            }
        }
    }
}

Expansion Player::GetExpByLevel()
{
    uint8 level = getLevel();

    if (level <= 60)
        return EXP_VANILLA;
    else if (level <= 70)
        return EXP_BC;
    else if (level <= 80)
        return EXP_WOTLK;
    else if (level <= 85)
        return EXP_CATACLYSM;
    else if (level <= 90)
        return EXP_PANDARIA;
    else
        return EXP_VANILLA;
}

void Player::RewardGuildReputation(Quest const* quest)
{
    uint32 rep = 0;

    switch (GetExpByLevel())
    {
        case EXP_VANILLA:   rep = 25;  break;
        case EXP_BC:        rep = 50;  break;
        case EXP_WOTLK:     rep = 75;  break;
        case EXP_CATACLYSM: rep = 100; break;
        case EXP_PANDARIA:  rep = 150; break;
        default:            rep = 0;   break;
    }

    rep = CalculateReputationGain(GetQuestLevel(quest), rep, REP_GUILD, true);

    if (GetsAFriendBonus(false))
        rep = int32(rep * (1 + sWorld->getRate(RATE_REPUTATION_RECRUIT_A_FRIEND_BONUS)));

    if (Guild* guild = sGuildMgr->GetGuildById(GetGuildId()))
        guild->RepGainedBy(this, rep);

    //if (FactionEntry const* factionEntry = sFactionStore.LookupEntry(REP_GUILD))
        //GetReputationMgr().ModifyReputation(factionEntry, rep);
}

void Player::UpdateHonorFields()
{
    /// called when rewarding honor and at each save
    time_t now = time_t(time(NULL));
    time_t today = time_t(time(NULL) / DAY) * DAY;

    if (m_lastHonorUpdateTime < today)
    {
        time_t yesterday = today - DAY;

        uint16 kills_today = PAIR32_LOPART(GetUInt32Value(PLAYER_FIELD_KILLS));

        // update yesterday's contribution
        if (m_lastHonorUpdateTime >= yesterday)
        {
            // this is the first update today, reset today's contribution
            SetUInt32Value(PLAYER_FIELD_KILLS, MAKE_PAIR32(0, kills_today));
        }
        else
        {
            // no honor/kills yesterday or today, reset
            SetUInt32Value(PLAYER_FIELD_KILLS, 0);
        }
        // clear all of today's kills, they will be flushed from the DB on next save
        m_killsPerPlayer.clear();
        m_flushKills = true;
    }

    m_lastHonorUpdateTime = now;
}

///Calculate the amount of honor gained based on the victim
///and the size of the group for which the honor is divided
///An exact honor value can also be given (overriding the calcs)
bool Player::RewardHonor(Unit* victim, uint32 groupsize, int32 honor, bool pvptoken)
{
    // do not reward honor in arenas, but enable onkill spellproc
    if (InArena())
    {
        if (!victim || victim == this || victim->GetTypeId() != TYPEID_PLAYER)
            return false;

        if (GetBGTeam() == victim->ToPlayer()->GetBGTeam())
            return false;

        return true;
    }

    // 'Inactive' this aura prevents the player from gaining honor points and battleground tokens
    if (HasAura(SPELL_AURA_PLAYER_INACTIVE))
        return false;

    uint64 victim_guid = 0;
    uint32 victim_rank = 0;

    // need call before fields update to have chance move yesterday data to appropriate fields before today data change.
    UpdateHonorFields();

    // do not reward honor in arenas, but return true to enable onkill spellproc
    if (InBattleground() && GetBattleground() && GetBattleground()->isArena())
        return true;

    // Promote to float for calculations
    float honor_f = (float)honor;

    if (honor_f <= 0)
    {
        if (!victim || victim == this || victim->HasAuraType(SPELL_AURA_NO_PVP_CREDIT))
            return false;

        victim_guid = victim->GetGUID();

        if (Player* plrVictim = victim->ToPlayer())
        {
            if (GetTeam() == plrVictim->GetTeam() && (!sWorld->IsFFAPvPRealm() && 
            !(InBattleground() && sWorld->getBoolConfig(CONFIG_CROSSFACTIONBG))))
                return false;

            uint8 k_level = getLevel();
            uint8 k_grey = Trinity::XP::GetGrayLevel(k_level);
            uint8 v_level = victim->getLevel();

            if (v_level <= k_grey)
                return false;

            // PLAYER_CHOSEN_TITLE VALUES DESCRIPTION
            //  [0]      Just name
            //  [1..14]  Alliance honor titles and player name
            //  [15..28] Horde honor titles and player name
            //  [29..38] Other title and player name
            //  [39+]    Nothing
            uint32 victim_title = victim->GetUInt32Value(PLAYER_CHOSEN_TITLE);
                                                        // Get Killer titles, CharTitlesEntry::bit_index
            // Ranks:
            //  title[1..14]  -> rank[5..18]
            //  title[15..28] -> rank[5..18]
            //  title[other]  -> 0
            if (victim_title == 0)
                victim_guid = 0;                        // Don't show HK: <rank> message, only log.
            else if (victim_title < 15)
                victim_rank = victim_title + 4;
            else if (victim_title < 29)
                victim_rank = victim_title - 14 + 4;
            else
                victim_guid = 0;                        // Don't show HK: <rank> message, only log.

            honor_f = ceil(Trinity::Honor::hk_honor_at_level_f(k_level) * (v_level - k_grey) / (k_level - k_grey));

            // count the number of playerkills in one day
            ApplyModUInt32Value(PLAYER_FIELD_KILLS, 1, true);
            // and those in a lifetime
            ApplyModUInt32Value(PLAYER_FIELD_LIFETIME_HONORABLE_KILLS, 1, true);
            UpdateAchievementCriteria(CRITERIA_TYPE_EARN_HONORABLE_KILL);
            UpdateAchievementCriteria(CRITERIA_TYPE_HK_CLASS, victim->getClass());
            UpdateAchievementCriteria(CRITERIA_TYPE_HK_RACE, victim->getRace());
            UpdateAchievementCriteria(CRITERIA_TYPE_HONORABLE_KILL_AT_AREA, GetAreaId());
            UpdateAchievementCriteria(CRITERIA_TYPE_HONORABLE_KILL, 1, 0, 0, victim);

            if (Guild* guild = sGuildMgr->GetGuildById(GetGuildId()))
                guild->GetAchievementMgr().UpdateAchievementCriteria(CRITERIA_TYPE_HONORABLE_KILLS_GUILD, 1, 0, 0, victim, this);

            m_saveKills = true;
        }
        else
        {
            if (!victim->ToCreature()->isRacialLeader())
                return false;

            honor_f = 100.0f;                               // ??? need more info
            victim_rank = 19;                               // HK: Leader
        }
    }

    if (victim != NULL)
    {
        honor_f *= sWorld->getRate(RATE_HONOR);

        if (groupsize > 1)
            honor_f /= groupsize;

        // apply honor multiplier from aura (not stacking-get highest)
        // AddPct(honor_f, GetMaxPositiveAuraModifier(SPELL_AURA_MOD_HONOR_GAIN_PCT));
    }
    else
    {
        honor_f *= sWorld->getRate(RATE_HONOR_QB);
    }

    // Back to int now
    honor = int32(honor_f);

    if (!InBattleground() && GetTypeId() == TYPEID_PLAYER && (victim) && victim->GetTypeId() == TYPEID_PLAYER)
    {
        uint8 limit = 6;

        KillInfo &info = m_killsPerPlayer[victim->GetGUIDLow()];
        // gradually decrease the honor for subsequent kills
        // no honor reward for killing a player more than 'limit' times per day
        honor *= (float)(limit - info.count) / (float)limit;
        // if the kill is not present in the DB keep in new state so it will be insterted
        // otherwise adding a kill means it will need to be updated
        if(info.state != KILL_NEW)
            info.state = KILL_CHANGED;

        // count the number of times a certain player was killed in one day
        if(info.count < limit)
            ++info.count;
    }

    if (victim)
    {
        AuraEffectList const& mMultipliers = GetAuraEffectsByType(SPELL_AURA_MOD_CURRENCY_GAIN_FROM_CREATURE);
        for (AuraEffectList::const_iterator itr = mMultipliers.begin(); itr != mMultipliers.end(); ++itr)
        {
            if ((*itr)->GetMiscValueB() == victim->GetCreatureType() && (*itr)->GetMiscValue() == CURRENCY_TYPE_HONOR_POINTS)
                AddPct(honor, (*itr)->GetAmount());
        }
    }

    // honor - for show honor points in log
    // victim_guid - for show victim name in log
    // victim_rank [1..4]  HK: <dishonored rank>
    // victim_rank [5..19] HK: <alliance\horde rank>
    // victim_rank [0, 20+] HK: <>
    WorldPacket data(SMSG_PVP_CREDIT, 4 + 8 + 4 + 1);
    data.WriteGuidMask<0, 3, 6, 4, 5, 2, 1, 7>(victim_guid);
    data.WriteGuidBytes<0, 7, 1, 6, 2>(victim_guid);
    data << uint32(honor);
    data.WriteGuidBytes<5, 3, 4>(victim_guid);
    data << uint32(victim_rank);

    GetSession()->SendPacket(&data);

    // add honor points
    ModifyCurrency(CURRENCY_TYPE_HONOR_POINTS, int32(honor));

    if (InBattleground() && honor > 0)
    {
        if (Battleground* bg = GetBattleground())
        {
            bg->UpdatePlayerScore(this, SCORE_BONUS_HONOR, honor, false); //false: prevent looping
        }
    }

    if (sWorld->getBoolConfig(CONFIG_PVP_TOKEN_ENABLE) && pvptoken)
    {
        if (!victim || victim == this || victim ->HasAuraType(SPELL_AURA_NO_PVP_CREDIT))
            return true;

        if (victim->GetTypeId() == TYPEID_PLAYER)
        {
            // Check if allowed to receive it in current map
            uint8 MapType = sWorld->getIntConfig(CONFIG_PVP_TOKEN_MAP_TYPE);
            if ((MapType == 1 && !InBattleground() && !HasByteFlag(UNIT_FIELD_BYTES_2, 1, UNIT_BYTE2_FLAG_FFA_PVP))
                || (MapType == 2 && !HasByteFlag(UNIT_FIELD_BYTES_2, 1, UNIT_BYTE2_FLAG_FFA_PVP))
                || (MapType == 3 && !InBattleground()))
                return true;

            uint32 itemId = sWorld->getIntConfig(CONFIG_PVP_TOKEN_ID);
            int32 count = sWorld->getIntConfig(CONFIG_PVP_TOKEN_COUNT);

            if (AddItem(itemId, count))
                ChatHandler(this).PSendSysMessage("You have been awarded a token for slaying another player.");
        }
    }

    return true;
}

void Player::_LoadCurrency(PreparedQueryResult result)
{
    if (!result)
        return;

    do
    {
        Field* fields = result->Fetch();

        uint16 currencyID = fields[0].GetUInt16();

        CurrencyTypesEntry const* currency = sCurrencyTypesStore.LookupEntry(currencyID);
        if (!currency)
        {
            CharacterDatabase.PExecute("DELETE FROM character_currency WHERE currency = '%u'", currencyID);
            continue;
        }

        PlayerCurrency cur;
        cur.state       = PLAYERCURRENCY_UNCHANGED;
        cur.weekCount   = fields[1].GetUInt32();
        cur.totalCount  = fields[2].GetUInt32();
        cur.seasonTotal = fields[3].GetUInt32();
        uint8 flags     = fields[4].GetUInt8();
        cur.curentCap   = fields[5].GetUInt32();

        cur.flags = flags & PLAYERCURRENCY_MASK_USED_BY_CLIENT;
        cur.currencyEntry = currency;

        _currencyStorage.insert(PlayerCurrenciesMap::value_type(currencyID, cur));

    } while (result->NextRow());
}

void Player::_SaveCurrency(SQLTransaction& trans)
{
    PreparedStatement* stmt = NULL;
    for (PlayerCurrenciesMap::iterator itr = _currencyStorage.begin(); itr != _currencyStorage.end(); ++itr)
    {
        CurrencyTypesEntry const* entry = sCurrencyTypesStore.LookupEntry(itr->first);
        if (!entry) // should never happen
            continue;

        uint32 curentCap = itr->second.curentCap;

        if (int32(curentCap) < 0)
            curentCap = GetCurrencyWeekCap(entry);

        switch(itr->second.state)
        {
        case PLAYERCURRENCY_NEW:
            stmt = CharacterDatabase.GetPreparedStatement(CHAR_REP_PLAYER_CURRENCY);
            stmt->setUInt32(0, GetGUIDLow());
            stmt->setUInt16(1, itr->first);
            stmt->setUInt32(2, itr->second.weekCount);
            stmt->setUInt32(3, itr->second.totalCount);
            stmt->setUInt32(4, itr->second.seasonTotal);
            stmt->setUInt8(5, itr->second.flags);
            stmt->setUInt32(6, curentCap);
            trans->Append(stmt);
            break;
        case PLAYERCURRENCY_CHANGED:
            stmt = CharacterDatabase.GetPreparedStatement(CHAR_UPD_PLAYER_CURRENCY);
            stmt->setUInt32(0, itr->second.weekCount);
            stmt->setUInt32(1, itr->second.totalCount);
            stmt->setUInt32(2, itr->second.seasonTotal);
            stmt->setUInt8(3, itr->second.flags);
            stmt->setUInt32(4, curentCap);
            stmt->setUInt32(5, GetGUIDLow());
            stmt->setUInt16(6, itr->first);
            trans->Append(stmt);
            break;
        default:
            break;
        }

        itr->second.state = PLAYERCURRENCY_UNCHANGED;
    }
}

void Player::SendCurrencies()
{
    ByteBuffer currencyData;

    //! 5.4.1
    WorldPacket packet(SMSG_INIT_CURRENCY, 3 + _currencyStorage.size() * (5 * 5 + 1));
    packet.WriteBits(_currencyStorage.size(), 21);

    for (PlayerCurrenciesMap::const_iterator itr = _currencyStorage.begin(); itr != _currencyStorage.end(); ++itr)
    {
        CurrencyTypesEntry const* entry = itr->second.currencyEntry;

        uint32 precision = entry->GetPrecision();
        uint32 weekCount = itr->second.weekCount;
        uint32 weekCap = GetCurrencyWeekCap(entry);
        uint32 seasonTotal = itr->second.seasonTotal;
        bool hasSeason = entry->HasSeasonCount();

        packet.WriteBit(hasSeason);                                     // season total earned
        packet.WriteBit(weekCap);
        packet.WriteBits(itr->second.flags, 5);                         // some flags
        packet.WriteBit(weekCap && weekCount);                          // hasWeekCount

        currencyData << uint32(itr->second.totalCount / precision);     // Currency count
        currencyData << uint32(entry->ID);                              // Currency Id

        if (hasSeason)
            currencyData << uint32(seasonTotal / precision);
        if (weekCap && weekCount)
            currencyData << uint32(weekCount / precision);
        if (weekCap)
            currencyData << uint32(weekCap / precision);
    }

    packet.FlushBits();
    if (!currencyData.empty())
        packet.append(currencyData);

    GetSession()->SendPacket(&packet);
}

void Player::ModifyCurrencyFlag(uint32 id, uint8 flag)
{
    if (!id)
        return;

    if (_currencyStorage.find(id) == _currencyStorage.end())
        return;

    _currencyStorage[id].flags = flag;
    if (_currencyStorage[id].state != PLAYERCURRENCY_NEW)
        _currencyStorage[id].state = PLAYERCURRENCY_CHANGED;
}

//! 5.4.1
void Player::SendPvpRewards()
{
    WorldPacket packet(SMSG_REQUEST_PVP_REWARDS_RESPONSE, 40);
    packet << uint32(GetCurrencyOnWeek(CURRENCY_TYPE_CONQUEST_POINTS) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_POINTS));
    packet << uint32(GetCurrencyOnWeek(CURRENCY_TYPE_CONQUEST_META_ARENA) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_ARENA));//packet << uint32(GetCurrencyOnWeek(CURRENCY_TYPE_CONQUEST_META_RANDOM_BG)  / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_RANDOM_BG));
    packet << uint32(sWorld->getIntConfig(CONFIG_CURRENCY_CONQUEST_POINTS_ARENA_REWARD) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_ARENA));
    packet << uint32(GetCurrencyWeekCap(CURRENCY_TYPE_CONQUEST_POINTS) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_POINTS));
    packet << uint32(GetCurrencyWeekCap(CURRENCY_TYPE_CONQUEST_META_ARENA) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_ARENA));
    packet << uint32(GetCurrencyOnWeek(CURRENCY_TYPE_CONQUEST_META_ARENA) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_ARENA));
    packet << uint32(GetCurrencyWeekCap(CURRENCY_TYPE_CONQUEST_META_ARENA) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_ARENA));//packet << uint32(GetCurrencyWeekCap(CURRENCY_TYPE_CONQUEST_META_RANDOM_BG) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_RANDOM_BG));
    packet << uint32(GetCurrencyOnWeek(CURRENCY_TYPE_CONQUEST_META_RATED_BG) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_RATED_BG));
    packet << uint32(GetCurrencyWeekCap(CURRENCY_TYPE_CONQUEST_META_RATED_BG) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_RATED_BG));
    packet << uint32(sWorld->getIntConfig(CONFIG_CURRENCY_CONQUEST_POINTS_RBG_REWARD) / GetCurrencyPrecision(CURRENCY_TYPE_CONQUEST_META_RATED_BG));
    GetSession()->SendPacket(&packet);
}

