void Player::SendInitWorldTimers()
{
    WorldPacket data(SMSG_WORLD_STATE_TIMER_START_INIT, 11);
    uint32 bpos = data.bitwpos();                           // Place Holder

    data.WriteBits(0, 21);                                  // count of uint64 blocks
    data.FlushBits();
    size_t countPos = data.wpos();

    if (InstanceScript* instance = GetInstanceScript())
        instance->FillInitialWorldTimers(data);

    uint16 length = (data.wpos() - countPos) / 8;
    data.PutBits<uint32>(bpos, length, 21);

    GetSession()->SendPacket(&data);
}

void Player::SendBGWeekendWorldStates()
{
    for (uint32 i = 1; i < sBattlemasterListStore.GetNumRows(); ++i)
    {
        BattlemasterListEntry const* bl = sBattlemasterListStore.LookupEntry(i);
        if (bl && bl->HolidayWorldStateId)
        {
            if (BattlegroundMgr::IsBGWeekend((BattlegroundTypeId)bl->id))
                SendUpdateWorldState(bl->HolidayWorldStateId, 1);
            else
                SendUpdateWorldState(bl->HolidayWorldStateId, 0);
        }
    }

    SendUpdateWorldState(WORLD_STATE_ENABLE_RATED_BG, 1);
}

uint32 Player::GetXPRestBonus(uint32 xp)
{
    uint32 rested_bonus = (uint32)GetRestBonus();           // xp for each rested bonus

    if (rested_bonus > xp)                                   // max rested_bonus == xp or (r+x) = 200% xp
        rested_bonus = xp;

    SetRestBonus(GetRestBonus() - rested_bonus);

    TC_LOG_DEBUG("player", "Player gain %u xp (+ %u Rested Bonus). Rested points=%f", xp+rested_bonus, rested_bonus, GetRestBonus());
    return rested_bonus;
}

void Player::SetBindPoint(uint64 guid)
{
    WorldPacket data(SMSG_BINDER_CONFIRM, 8);
    data.WriteGuidMask<6, 3, 4, 5, 1, 2, 7, 0>(guid);
    data.WriteGuidBytes<0, 3, 5, 2, 1, 7, 6, 4>(guid);

    GetSession()->SendPacket(&data);
}

void Player::SendTalentWipeConfirm(uint64 guid, bool specialization)
{
    ObjectGuid Guid = guid;
    uint32 cost = 0;

    if (!specialization)
        cost = sWorld->getBoolConfig(CONFIG_NO_RESET_TALENT_COST) ? 0 : GetNextResetTalentsCost();
    else
        cost = sWorld->getBoolConfig(CONFIG_NO_RESET_TALENT_COST) ? 0 : GetNextResetSpecializationCost();

    WorldPacket data(SMSG_RESPEC_WIPE_CONFIRM);
    data.WriteGuidMask<7, 4, 2, 3, 5, 6, 1, 0>(Guid);
    data << uint32(cost);
    data.WriteGuidBytes<1, 6, 7, 5, 0, 4, 3>(Guid);
    data << uint8(specialization ? 1 : 0);  // 0: talent, 1: specialization, 2: glyph, 3: pet speciazlization
    data.WriteGuidBytes<2>(Guid);

    GetSession()->SendPacket(&data);
}

