void Player::_LoadSpellRewards()
{
    // check on achievement reward spell
    CompletedAchievementMap const* completedAchievements = GetAchievementMgr().GetCompletedAchievementsList();

    if (completedAchievements->empty())
        return;

    for (CompletedAchievementMap::const_iterator iter = completedAchievements->begin(); iter != completedAchievements->end(); ++iter)
    {
        AchievementEntry const* achievement = sAchievementStore.LookupEntry(iter->first);
        if (!achievement)
            continue;

        if (AchievementReward const* reward = sAchievementMgr->GetAchievementReward(achievement))
        {
            if (reward->learnSpell && !HasSpell(reward->learnSpell))
                learnSpell(reward->learnSpell, true);
        }
    }
}

void Player::_LoadCUFProfiles(PreparedQueryResult result)
{
    if (!result)
        return;

    do
    {
        // SELECT profileId, profileName, frameHeight, frameWidth, sortBy, healthText, options, unk146, unk147, unk148, unk150, unk152, unk154 FROM character_cuf_profiles WHERE guid = ?
        Field* fields = result->Fetch();

        uint8 id           = fields[0].GetUInt8();
        std::string name   = fields[1].GetString();
        uint16 frameHeight = fields[2].GetUInt16();
        uint16 frameWidth  = fields[3].GetUInt16();
        uint8 sortBy       = fields[4].GetUInt8();
        uint8 healthText   = fields[5].GetUInt8();
        uint32 options = fields[6].GetUInt32();
        uint8 unk146       = fields[7].GetUInt8();
        uint8 unk147       = fields[8].GetUInt8();
        uint8 unk148       = fields[9].GetUInt8();
        uint16 unk150      = fields[10].GetUInt16();
        uint16 unk152      = fields[11].GetUInt16();
        uint16 unk154      = fields[12].GetUInt16();

        if (id > MAX_CUF_PROFILES)
        {
            TC_LOG_ERROR("player", "Player::_LoadCUFProfiles - Player (GUID: %u, name: %s) has an CUF profile with invalid id (id: %u), max is %i.", GetGUIDLow(), GetName(), id, MAX_CUF_PROFILES);
            continue;
        }

        _CUFProfiles[id] = new CUFProfile(name, frameHeight, frameWidth, sortBy, healthText, options, unk146, unk147, unk148, unk150, unk152, unk154);
    }
    while (result->NextRow());
}

void Player::_LoadBattlePets(PreparedQueryResult result)
{
    if (!result)
        return;

    do
    {
        // SELECT guid, customName, creatureEntry, speciesID, spell, level, displayID, power, speed, health, maxHealth, quality, xp, flags FROM character_battle_pet_journal WHERE ownerAccID = ?
        Field* fields = result->Fetch();

        uint64 guid        = fields[0].GetUInt64();
        std::string customName = fields[1].GetString();
        uint32 creatureEntry = fields[2].GetUInt32();
        uint32 speciesID = fields[3].GetUInt32();
        uint32 spell = fields[4].GetUInt32();
        uint8 level = fields[5].GetUInt8();
        uint32 displayID = fields[6].GetUInt32();
        uint16 power = fields[7].GetUInt16();
        uint16 speed = fields[8].GetUInt16();
        uint32 health = fields[9].GetUInt32();
        uint32 maxHealth = fields[10].GetUInt32();
        uint8 quality = fields[11].GetUInt8();
        uint16 xp = fields[12].GetUInt16();
        uint16 flags = fields[13].GetUInt16();
        int16 breedID = fields[14].GetUInt16();

        // recalculate stats after change breed
        if (!breedID)
        {
            breedID = GetBattlePetMgr()->GetRandomBreedID(speciesID);

            if (!breedID)
                continue;

            float pct = float(health * 100.0f) / maxHealth;

            BattlePetStatAccumulator* accumulator = new BattlePetStatAccumulator(speciesID, breedID);
            accumulator->CalcQualityMultiplier(quality, level);
            maxHealth = accumulator->CalculateHealth();
            power = accumulator->CalculatePower();
            speed = accumulator->CalculateSpeed();
            delete accumulator;

            if (pct != 100.0f)
                health = int32(maxHealth * float(pct / 100.0f));
            else
                health = maxHealth;
        }

        // prevent some undefined behavoir
        if (health > maxHealth)
            health = maxHealth;

        GetBattlePetMgr()->AddPetToList(guid, speciesID, creatureEntry, level, displayID, power, speed, health, maxHealth, quality, xp, flags, spell, customName, breedID, STATE_NORMAL);
        UpdateAchievementCriteria(CRITERIA_TYPE_ADD_BATTLE_PET_JOURNAL, creatureEntry);
    }
    while (result->NextRow());
}

void Player::_LoadBattlePetSlots(PreparedQueryResult result)
{
    if (!result)
    {
        // initial first
        for (int i = 0; i < MAX_ACTIVE_BATTLE_PETS; ++i)
            GetBattlePetMgr()->InitBattleSlot(0, i);
        return;
    }

    // SELECT slot_0, slot_1, slot_2 FROM character_battle_pet WHERE ownerAccID = ?
    Field* fields = result->Fetch();

    uint64 petGUIDs[3] = {0, 0, 0};

    petGUIDs[0]  = fields[0].GetUInt64();
    petGUIDs[1]  = fields[1].GetUInt64();
    petGUIDs[2]  = fields[2].GetUInt64();

    for (int i = 0; i < MAX_ACTIVE_BATTLE_PETS; ++i)
        GetBattlePetMgr()->InitBattleSlot(petGUIDs[i], i);
}

void Player::_LoadGroup(PreparedQueryResult result)
{
    //QueryResult* result = CharacterDatabase.PQuery("SELECT guid FROM group_member WHERE memberGuid=%u", GetGUIDLow());
    if (result)
    {
        if (Group* group = sGroupMgr->GetGroupByDbStoreId((*result)[0].GetUInt32()))
        {
            uint8 subgroup = group->GetMemberGroup(GetGUID());
            SetGroup(group, subgroup);
            if (getLevel() >= LEVELREQUIREMENT_HEROIC)
            {
                // the group leader may change the instance difficulty while the player is offline
                SetDungeonDifficulty(group->GetDungeonDifficulty());
                SetRaidDifficulty(group->GetRaidDifficulty());
            }
        }
    }
}

void Player::_LoadLootCooldown(PreparedQueryResult result)
{
    //QueryResult* result = CharacterDatabase.PQuery("SELECT entry, bonus, respawnTime FROM character_creature WHERE guid = %u", GetGUIDLow());
    if (!result)
        return;
    do
    {
        Field* fields = result->Fetch();
        playerLootCooldown lootCooldown;
        lootCooldown.entry = fields[0].GetInt32();
        lootCooldown.type = fields[1].GetInt8();
        lootCooldown.difficultyMask = fields[2].GetInt8();
        lootCooldown.respawnTime = fields[3].GetInt32();
        lootCooldown.state = false;
        m_playerLootCooldown[lootCooldown.type][lootCooldown.entry] = lootCooldown;
    }
    while (result->NextRow());
}

void Player::_SaveLootCooldown(SQLTransaction& trans)
{
    PreparedStatement* stmt = NULL;

    for(int i = 0; i < MAX_LOOT_COOLDOWN_TYPE; i++)
    {
        for (PlayerLootCooldownMap::iterator itr = m_playerLootCooldown[i].begin(); itr != m_playerLootCooldown[i].end(); ++itr)
        {
            if(!itr->second.state)
                continue;

            stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_PLAYER_LOOTCOOLDOWN);
            stmt->setUInt64(0, GetGUIDLow());
            stmt->setUInt32(1, itr->second.entry);
            stmt->setUInt32(2, itr->second.type);
            stmt->setUInt32(3, itr->second.difficultyMask);
            stmt->setUInt32(4, itr->second.respawnTime);
            itr->second.state = false;
            trans->Append(stmt);
        }
    }
}

void Player::ResetLootCooldown()
{
    for(int i = 0; i < MAX_LOOT_COOLDOWN_TYPE; ++i)
    {
        for (PlayerLootCooldownMap::iterator itr = m_playerLootCooldown[i].begin(); itr != m_playerLootCooldown[i].end();)
        {
            if(itr->second.respawnTime)
                m_playerLootCooldown[i].erase(itr++);
            else
                ++itr;
        }
    }
}

void Player::AddPlayerLootCooldown(uint32 entry, uint8 type/* = 0*/, bool respawn/* = true*/, uint8 diff/* = 0*/)
{
    PlayerLootCooldownMap::iterator itr = m_playerLootCooldown[type].find(entry);
    if(itr == m_playerLootCooldown[type].end())
    {
        playerLootCooldown lootCooldown;
        lootCooldown.entry = entry;
        lootCooldown.type = type;
        lootCooldown.difficultyMask = (1 << (sObjectMgr->GetDiffFromSpawn(diff)));
        lootCooldown.respawnTime = respawn ? time(NULL) + WEEK : 0;
        lootCooldown.state = true;
        m_playerLootCooldown[type][entry] = lootCooldown;
    }
    else
        itr->second.difficultyMask |= (1 << (sObjectMgr->GetDiffFromSpawn(diff)));
}

bool Player::IsPlayerLootCooldown(uint32 entry, uint8 type/* = 0*/, uint8 diff/* = 0*/) const
{
    PlayerLootCooldownMap::const_iterator itr = m_playerLootCooldown[type].find(entry);
    if (itr == m_playerLootCooldown[type].end())
        return false;

    if (itr->second.difficultyMask & (1 << (sObjectMgr->GetDiffFromSpawn(diff))))
        return true;

    return false;
}

void Player::_LoadBoundInstances(PreparedQueryResult result)
{
    for (uint8 i = 0; i < MAX_BOUND; ++i)
        m_boundInstances[i].clear();

    Group* group = GetGroup();

    //QueryResult* result = CharacterDatabase.PQuery("SELECT id, permanent, map, difficulty, completedEncounters FROM character_instance LEFT JOIN instance ON instance = id WHERE guid = '%u'", GUID_LOPART(m_guid));
    if (result)
    {
        do
        {
            Field* fields = result->Fetch();

            bool perm = fields[1].GetBool();
            uint32 mapId = fields[2].GetUInt16();
            uint32 instanceId = fields[0].GetUInt32();
            uint8 difficulty = fields[3].GetUInt8();
            uint32 completedEncounter = fields[4].GetUInt32();

            bool deleteInstance = false;

            MapEntry const* mapEntry = sMapStore.LookupEntry(mapId);
            if (!mapEntry || !mapEntry->IsDungeon())
            {
                TC_LOG_ERROR("player", "_LoadBoundInstances: player %s(%d) has bind to not existed or not dungeon map %d", GetName(), GetGUIDLow(), mapId);
                deleteInstance = true;
            }
            else if (difficulty >= MAX_DIFFICULTY)
            {
                TC_LOG_ERROR("player", "_LoadBoundInstances: player %s(%d) has bind to not existed difficulty %d instance for map %u", GetName(), GetGUIDLow(), difficulty, mapId);
                deleteInstance = true;
            }
            else
            {
                MapDifficulty const* mapDiff = GetMapDifficultyData(mapId, Difficulty(difficulty));
                if (!mapDiff)
                {
                    TC_LOG_ERROR("player", "_LoadBoundInstances: player %s(%d) has bind to not existed difficulty %d instance for map %u", GetName(), GetGUIDLow(), difficulty, mapId);
                    deleteInstance = true;
                }
                else if (!perm && group)
                {
                    TC_LOG_ERROR("player", "_LoadBoundInstances: player %s(%d) is in group %d but has a non-permanent character bind to map %d, %d, %d", GetName(), GetGUIDLow(), GUID_LOPART(group->GetGUID()), mapId, instanceId, difficulty);
                    deleteInstance = true;
                }
            }

            if (deleteInstance)
            {
                PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_CHAR_INSTANCE_BY_INSTANCE_GUID);

                stmt->setUInt32(0, GetGUIDLow());
                stmt->setUInt32(1, instanceId);

                CharacterDatabase.Execute(stmt);

                continue;
            }

            // since non permanent binds are always solo bind, they can always be reset
            if (InstanceSave* save = sInstanceSaveMgr->AddInstanceSave(mapId, instanceId, Difficulty(difficulty), !perm, true))
            {
                save->SetCompletedEncountersMask(completedEncounter);
                BindToInstance(save, perm, true);
            }
        }
        while (result->NextRow());
    }
}

InstancePlayerBind* Player::GetBoundInstance(uint32 mapid, Difficulty difficulty)
{
    // some instances only have one difficulty
    MapDifficulty const* mapDiff = GetDownscaledMapDifficultyData(mapid, difficulty);
    if (!mapDiff)
        return NULL;

    uint8 boundType = sObjectMgr->GetboundTypeFromDifficulty(difficulty);

    BoundInstancesMap::iterator itr = m_boundInstances[boundType].find(mapid);
    if (itr != m_boundInstances[boundType].end())
        return &itr->second;
    else
        return NULL;
}

InstanceSave* Player::GetInstanceSave(uint32 mapid, bool raid)
{
    InstancePlayerBind* pBind = GetBoundInstance(mapid, GetDifficulty(raid));
    InstanceSave* pSave = pBind ? pBind->save : NULL;
    if (!pBind || !pBind->perm)
        if (Group* group = GetGroup())
            if (InstanceGroupBind* groupBind = group->GetBoundInstance(this))
                pSave = groupBind->save;

    return pSave;
}

void Player::UnbindInstance(uint32 mapid, Difficulty difficulty, bool unload)
{
    uint8 boundType = sObjectMgr->GetboundTypeFromDifficulty(difficulty);
    BoundInstancesMap::iterator itr = m_boundInstances[boundType].find(mapid);
    UnbindInstance(itr, difficulty, unload);
}

void Player::UnbindInstance(BoundInstancesMap::iterator &itr, Difficulty difficulty, bool unload)
{
    uint8 boundType = sObjectMgr->GetboundTypeFromDifficulty(difficulty);
    if (itr != m_boundInstances[boundType].end())
    {
        if (!unload)
        {
            PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_CHAR_INSTANCE_BY_INSTANCE_GUID);

            stmt->setUInt32(0, GetGUIDLow());
            stmt->setUInt32(1, itr->second.save->GetInstanceId());

            CharacterDatabase.Execute(stmt);
        }

        if (itr->second.perm)
            GetSession()->SendCalendarRaidLockoutRemoved(itr->second.save);

        itr->second.save->RemovePlayer(this);               // save can become invalid
        m_boundInstances[boundType].erase(itr++);
    }
}

InstancePlayerBind* Player::BindToInstance(InstanceSave* save, bool permanent, bool load)
{
    if (save)
    {
        uint8 boundType = sObjectMgr->GetboundTypeFromDifficulty(save->GetDifficulty());
        InstancePlayerBind& bind = m_boundInstances[boundType][save->GetMapId()];
        if(!save->CanBeSave())
            permanent = false;
        if(save->CanBeSave())
        {
            if (bind.save)
            {
                // update the save when the group kills a boss
                if (permanent != bind.perm || save != bind.save)
                    if (!load)
                    {
                        PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_UPD_CHAR_INSTANCE);

                        stmt->setUInt32(0, save->GetInstanceId());
                        stmt->setBool(1, permanent);
                        stmt->setUInt32(2, GetGUIDLow());
                        stmt->setUInt32(3, bind.save->GetInstanceId());

                        CharacterDatabase.Execute(stmt);
                    }
            }
            else
                if (!load)
                {
                    PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_CHAR_INSTANCE);

                    stmt->setUInt32(0, GetGUIDLow());
                    stmt->setUInt32(1, save->GetInstanceId());
                    stmt->setBool(2, permanent);

                    CharacterDatabase.Execute(stmt);
                }
        }

        if (bind.save != save)
        {
            if (bind.save)
                bind.save->RemovePlayer(this);
            save->AddPlayer(this);
        }

        if (permanent)
            save->SetCanReset(false);

        bind.save = save;
        bind.perm = permanent;
        if (!load)
            TC_LOG_DEBUG("maps", "Player::BindToInstance: %s(%d) is now bound to map %d, instance %d, difficulty %d", GetName(), GetGUIDLow(), save->GetMapId(), save->GetInstanceId(), save->GetDifficulty());
        sScriptMgr->OnPlayerBindToInstance(this, save->GetDifficulty(), save->GetMapId(), permanent);
        return &bind;
    }
    else
        return NULL;
}

//! 5.4.1
void Player::BindToInstance()
{
    InstanceSave* mapSave = sInstanceSaveMgr->GetInstanceSave(_pendingBindId);
    if (!mapSave) //it seems sometimes mapSave is NULL, but I did not check why
        return;

    WorldPacket data(SMSG_INSTANCE_SAVE_CREATED, 1);
    data << uint8(0);           //debug bit
    GetSession()->SendPacket(&data);
    BindToInstance(mapSave, true);

    GetSession()->SendCalendarRaidLockoutAdded(mapSave);
}

//! 5.4.1
void Player::SendRaidInfo()
{
    uint32 counter = 0;
    ObjectGuid instanceID = 0;
    ByteBuffer dataBuffer;
    InstanceSave* save = NULL;

    WorldPacket data(SMSG_RAID_INSTANCE_INFO, 200);
    data.WriteBits(counter, 20);                            // placeholder

    time_t now = time(NULL);

    for (uint8 i = 0; i < MAX_BOUND; ++i)
    {
        for (BoundInstancesMap::iterator itr = m_boundInstances[i].begin(); itr != m_boundInstances[i].end(); ++itr)
        {
            if (itr->second.perm)
            {
                save = itr->second.save;
                instanceID = save->GetInstanceId();

                uint32 timeReset = uint32(save->GetResetTime() - now);
                if (MapDifficulty const* mapDiff = GetMapDifficultyData(save->GetMapId(), save->GetDifficulty()))
                    timeReset = uint32(sWorld->getInstanceResetTime(mapDiff->resetTime) - now);

                data.WriteGuidMask<2, 4, 7>(instanceID);
                data.WriteBit(0);                                       //extended?
                data.WriteGuidMask<1, 6>(instanceID);
                data.WriteBit(1);                                       //expired?
                data.WriteGuidMask<5, 3, 0>(instanceID);

                dataBuffer.WriteGuidBytes<2>(instanceID);
                dataBuffer << uint32(timeReset);       // reset time
                dataBuffer.WriteGuidBytes<7, 5, 0, 4, 1, 3>(instanceID);
                dataBuffer << uint32(save->GetDifficulty());            // difficulty
                dataBuffer << uint32(save->GetMapId());                 // map id
                dataBuffer << uint32(save->GetCompletedEncounterMask());// CompletedEncounterMask
                dataBuffer.WriteGuidBytes<6>(instanceID);
                ++counter;
            }
        }
    }

    data.FlushBits();
    data.append(dataBuffer);

    data.PutBits<uint32>(0, counter, 20);
    GetSession()->SendPacket(&data);
}

/*
- called on every successful teleportation to a map
*/
void Player::SendSavedInstances()
{
    bool hasBeenSaved = false;
    WorldPacket data;

    for (uint8 i = 0; i < MAX_BOUND; ++i)
    {
        for (BoundInstancesMap::iterator itr = m_boundInstances[i].begin(); itr != m_boundInstances[i].end(); ++itr)
        {
            if (itr->second.perm)                               // only permanent binds are sent
            {
                hasBeenSaved = true;
                break;
            }
        }
    }

    //Send opcode SMSG_UPDATE_INSTANCE_OWNERSHIP. true or false means, whether you have current raid/heroic instances
    //! 5.4.1
    data.Initialize(SMSG_UPDATE_INSTANCE_OWNERSHIP, 4);
    data << uint32(hasBeenSaved);
    GetSession()->SendPacket(&data);

    if (!hasBeenSaved)
        return;

    for (uint8 i = 0; i < MAX_BOUND; ++i)
    {
        for (BoundInstancesMap::iterator itr = m_boundInstances[i].begin(); itr != m_boundInstances[i].end(); ++itr)
        {
            if (itr->second.perm)
            {
                //! 5.4.1
                data.Initialize(SMSG_UPDATE_LAST_INSTANCE, 4);
                data << uint32(itr->second.save->GetMapId());
                GetSession()->SendPacket(&data);
            }
        }
    }
}

/// convert the player's binds to the group
void Player::ConvertInstancesToGroup(Player* player, Group* group, bool switchLeader)
{
    // copy all binds to the group, when changing leader it's assumed the character
    // will not have any solo binds

    for (uint8 i = 0; i < MAX_BOUND; ++i)
    {
        for (BoundInstancesMap::iterator itr = player->m_boundInstances[i].begin(); itr != player->m_boundInstances[i].end();)
        {
            group->BindToInstance(itr->second.save, itr->second.perm, false);
            // permanent binds are not removed
            if (switchLeader && !itr->second.perm)
            {
                // increments itr in call
                player->UnbindInstance(itr, itr->second.save->GetDifficulty(), false);
            }
            else
                ++itr;
        }
    }
}

Player::BoundInstancesMap& Player::GetBoundInstances(Difficulty difficulty)
{
    uint8 boundType = sObjectMgr->GetboundTypeFromDifficulty(difficulty);
    return m_boundInstances[boundType];
}

bool Player::Satisfy(AccessRequirement const* ar, uint32 target_map, bool report)
{
    if (!isGameMaster() && ar)
    {
        uint8 LevelMin = 0;
        uint8 LevelMax = 0;

        MapEntry const* mapEntry = sMapStore.LookupEntry(target_map);
        if (!mapEntry)
            return false;

        if (!sWorld->getBoolConfig(CONFIG_INSTANCE_IGNORE_LEVEL))
        {
            if (ar->levelMin && getLevel() < ar->levelMin)
                LevelMin = ar->levelMin;
            if (ar->levelMax && getLevel() > ar->levelMax)
                LevelMax = ar->levelMax;
        }

        uint32 missingItem = 0;
        if (ar->item)
        {
            if (!HasItemCount(ar->item) &&
                (!ar->item2 || !HasItemCount(ar->item2)))
                missingItem = ar->item;
        }
        else if (ar->item2 && !HasItemCount(ar->item2))
            missingItem = ar->item2;

        if (ar->item_level)
        {
            if (GetAverageItemLevel() < ar->item_level)
                return false;
        }

        if (DisableMgr::IsDisabledFor(DISABLE_TYPE_MAP, target_map, this))
        {
            GetSession()->SendNotification("%s", GetSession()->GetTrinityString(LANG_INSTANCE_CLOSED));
            return false;
        }

        uint32 missingQuest = 0;
        uint32 checkAchievement = 0;
        if(GetTeam() == ALLIANCE)
        {
            if (ar->quest_A && !GetQuestRewardStatus(ar->quest_A))
                missingQuest = ar->quest_A;
            if(ar->achievement_A != 0)
                checkAchievement = ar->achievement_A;
        }
        else if (ar->quest_H && !GetQuestRewardStatus(ar->quest_H))
            missingQuest = ar->quest_H;

        if(checkAchievement == 0)
            checkAchievement = ar->achievement;

        Player* leader = this;
        uint64 leaderGuid = GetGroup() ? GetGroup()->GetLeaderGUID() : GetGUID();
        if (leaderGuid != GetGUID())
            leader = ObjectAccessor::FindPlayer(leaderGuid);

        uint32 missingAchievement = 0;
        if (checkAchievement)
            if (!leader || !leader->HasAchieved(checkAchievement))
                missingAchievement = checkAchievement;

        Difficulty target_difficulty = GetDifficulty(mapEntry->IsRaid());

        MapDifficulty const* mapDiff = GetDownscaledMapDifficultyData(target_map, target_difficulty);
        if (!mapDiff)
            return false;

        if (LevelMin || LevelMax || missingItem || missingQuest || missingAchievement)
        {
            if (report)
            {
                if (missingQuest && !ar->questFailedText.empty())
                    ChatHandler(GetSession()).PSendSysMessage("%s", ar->questFailedText.c_str());
                else if (missingAchievement)
                    SendTransferAborted(target_map, TRANSFER_ABORT_DIFFICULTY, target_difficulty);
                else if (missingItem)
                    GetSession()->SendNotification(GetSession()->GetTrinityString(LANG_LEVEL_MINREQUIRED_AND_ITEM), LevelMin, sObjectMgr->GetItemTemplate(missingItem)->Name1.c_str());
                else if (LevelMin)
                    GetSession()->SendNotification(GetSession()->GetTrinityString(LANG_LEVEL_MINREQUIRED), LevelMin);
            }
            return false;
        }
    }
    return true;
}

bool Player::CheckInstanceLoginValid()
{
    if (!GetMap())
        return false;

    MapEntry const* entry = sMapStore.LookupEntry(GetMap()->GetId());
    if (!GetMap()->IsDungeon() || isGameMaster())
        return true;

    // and do one more check before InstanceMap::CanEnte
    // instance full don't checks in CanPlayerEnter due ignore login case.
    if (GetMap()->GetPlayersCountExceptGMs() > ((InstanceMap*)GetMap())->GetMaxPlayers())
    {
        SendTransferAborted(GetMap()->GetId(), TRANSFER_ABORT_MAX_PLAYERS);
        return false;
    }

    // do checks for satisfy accessreqs, instance full, encounter in progress (raid), perm bind group != perm bind player
    return sMapMgr->CanPlayerEnter(GetMap()->GetId(), this, true);
}

bool Player::_LoadHomeBind(PreparedQueryResult result)
{
    PlayerInfo const* info = sObjectMgr->GetPlayerInfo(getRace(), getClass());
    if (!info)
    {
        TC_LOG_ERROR("player", "Player (Name %s) has incorrect race/class (%u/%u) pair. Can't be loaded.", GetName(), uint32(getRace()), uint32(getClass()));
        return false;
    }

    bool ok = false;
    // SELECT mapId, zoneId, posX, posY, posZ FROM character_homebind WHERE guid = ?
    if (result)
    {
        Field* fields = result->Fetch();

        m_homebindMapId = fields[0].GetUInt16();
        m_homebindAreaId = fields[1].GetUInt16();
        m_homebindX = fields[2].GetFloat();
        m_homebindY = fields[3].GetFloat();
        m_homebindZ = fields[4].GetFloat();

        MapEntry const* bindMapEntry = sMapStore.LookupEntry(m_homebindMapId);

        // accept saved data only for valid position (and non instanceable), and accessable
        if (MapManager::IsValidMapCoord(m_homebindMapId, m_homebindX, m_homebindY, m_homebindZ) &&
            !bindMapEntry->Instanceable() && GetSession()->Expansion() >= bindMapEntry->Expansion())
            ok = true;
        else
        {
            PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_PLAYER_HOMEBIND);
            stmt->setUInt32(0, GetGUIDLow());
            CharacterDatabase.Execute(stmt);
        }
    }

    if (!ok)
    {
        m_homebindMapId = info->mapId;
        m_homebindAreaId = info->areaId;
        m_homebindX = info->positionX;
        m_homebindY = info->positionY;
        m_homebindZ = info->positionZ;

        PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_PLAYER_HOMEBIND);
        stmt->setUInt32(0, GetGUIDLow());
        stmt->setUInt16(1, m_homebindMapId);
        stmt->setUInt16(2, m_homebindAreaId);
        stmt->setFloat (3, m_homebindX);
        stmt->setFloat (4, m_homebindY);
        stmt->setFloat (5, m_homebindZ);
        CharacterDatabase.Execute(stmt);
    }

    TC_LOG_DEBUG("player", "Setting player home position - mapid: %u, areaid: %u, X: %f, Y: %f, Z: %f",
        m_homebindMapId, m_homebindAreaId, m_homebindX, m_homebindY, m_homebindZ);

    return true;
}

