void Player::_SaveSkills(SQLTransaction& trans)
{
    PreparedStatement* stmt = NULL;
    // we don't need transactions here.
    for (SkillStatusMap::iterator itr = mSkillStatus.begin(); itr != mSkillStatus.end();)
    {
        if (itr->second.uState == SKILL_UNCHANGED)
        {
            ++itr;
            continue;
        }

        if (itr->second.uState == SKILL_DELETED)
        {
            stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_CHAR_SKILL_BY_SKILL);
            stmt->setUInt32(0, GetGUIDLow());
            stmt->setUInt32(1, itr->first);
            trans->Append(stmt);

            mSkillStatus.erase(itr++);
            continue;
        }

        uint16 field = itr->second.pos / 2;
        uint8 offset = itr->second.pos & 1;

        uint16 value = GetUInt16Value(PLAYER_SKILL_RANK_0 + field, offset);
        uint16 max = GetUInt16Value(PLAYER_SKILL_MAX_RANK_0 + field, offset);

        switch (itr->second.uState)
        {
            case SKILL_NEW:
                stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_CHAR_SKILLS);
                stmt->setUInt32(0, GetGUIDLow());
                stmt->setUInt16(1, uint16(itr->first));
                stmt->setUInt16(2, value);
                stmt->setUInt16(3, max);
                trans->Append(stmt);
                break;
            case SKILL_CHANGED:
                stmt = CharacterDatabase.GetPreparedStatement(CHAR_UDP_CHAR_SKILLS);
                stmt->setUInt16(0, value);
                stmt->setUInt16(1, max);
                stmt->setUInt32(2, GetGUIDLow());
                stmt->setUInt16(3, uint16(itr->first));
                trans->Append(stmt);
                break;
            default:
                break;
        }

        itr->second.uState = SKILL_UNCHANGED;
        ++itr;
    }
}

void Player::_SaveSpells(SQLTransaction& trans)
{
    PreparedStatement* stmt = NULL;

    for (PlayerSpellMap::iterator itr = m_spells.begin(); itr != m_spells.end();)
    {
        if(!itr->second)
        {
            m_spells.erase(itr++);
            continue;
        }

        if (itr->second->state == PLAYERSPELL_TEMPORARY)
        {
            itr++;
            continue;
        }

        if (itr->second->state == PLAYERSPELL_REMOVED || itr->second->state == PLAYERSPELL_CHANGED)
        {
            if(itr->second->mount)
            {
                stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_ACC_SPELL_BY_SPELL);
                stmt->setUInt32(0, itr->first);
                stmt->setUInt32(1, GetSession()->GetAccountId());
                trans->Append(stmt);
            }
            else
            {
                stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_CHAR_SPELL_BY_SPELL);
                stmt->setUInt32(0, itr->first);
                stmt->setUInt32(1, GetGUIDLow());
                trans->Append(stmt);
            }
        }

        // add only changed/new not dependent spells
        if (!itr->second->dependent && (itr->second->state == PLAYERSPELL_NEW || itr->second->state == PLAYERSPELL_CHANGED))
        {
            if(itr->second->mount)
            {
                stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_ACC_MOUNT);
                stmt->setUInt32(0, GetSession()->GetAccountId());
                stmt->setUInt32(1, itr->first);
                stmt->setBool(2, itr->second->active);
                trans->Append(stmt);
            }
            else
            {
                stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_CHAR_SPELL);
                stmt->setUInt32(0, GetGUIDLow());
                stmt->setUInt32(1, itr->first);
                stmt->setBool(2, itr->second->active);
                stmt->setBool(3, itr->second->disabled);
                trans->Append(stmt);
            }
        }

        if (itr->second->state == PLAYERSPELL_REMOVED)
        {
            delete itr->second;
            m_spells.erase(itr++);
        }
        else
        {
            itr->second->state = PLAYERSPELL_UNCHANGED;
            ++itr;
        }
    }
}

void Player::_SaveCUFProfiles(SQLTransaction& trans)
{
    uint32 playerGuid = GetGUIDLow();

    for (uint8 i = 0; i < MAX_CUF_PROFILES; ++i)
    {
        // delete unused profile
        if (!_CUFProfiles[i])
            trans->PAppend("DELETE FROM character_cuf_profiles WHERE guid = '%u' and profileId = '%u'", playerGuid, i);
        else
        {
            // REPLACE INTO character_cuf_profiles (guid, profileId, profileName, frameHeight, frameWidth, sortBy, healthText, boolOptions, unk146, unk147, unk148, unk150, unk152, unk154) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
            PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_REP_CUF_PROFILES);
            stmt->setUInt32(0, playerGuid);
            stmt->setUInt8(1, i);
            stmt->setString(2, _CUFProfiles[i]->profileName);
            stmt->setUInt16(3, _CUFProfiles[i]->frameHeight);
            stmt->setUInt16(4, _CUFProfiles[i]->frameWidth);
            stmt->setUInt8(5, _CUFProfiles[i]->sortBy);
            stmt->setUInt8(6, _CUFProfiles[i]->showHealthText);
            stmt->setUInt32(7, _CUFProfiles[i]->options);
            stmt->setUInt8(8, _CUFProfiles[i]->Unk146);
            stmt->setUInt8(9, _CUFProfiles[i]->Unk147);
            stmt->setUInt8(10, _CUFProfiles[i]->Unk148);
            stmt->setUInt16(11, _CUFProfiles[i]->Unk150);
            stmt->setUInt16(12, _CUFProfiles[i]->Unk152);
            stmt->setUInt16(13, _CUFProfiles[i]->Unk154);

            trans->Append(stmt);
        }
    }
}

void Player::_SaveBattlePets(SQLTransaction& trans)
{
    PetJournal journal = GetBattlePetMgr()->GetPetJournal();

    // nothing to save
    if (journal.empty())
        return;

    // save journal
    for (PetJournal::const_iterator pet = journal.begin(); pet != journal.end(); ++pet)
    {
        PetJournalInfo* pjInfo = pet->second;

        if (!pjInfo)
            continue;

        if (pjInfo->GetState() == STATE_DELETED)
        {
            PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_ACCOUNT_BATTLE_PET_LIST);
            stmt->setUInt32(0, GetSession()->GetAccountId());
            stmt->setUInt64(1, pet->first);
            trans->Append(stmt);
        }
        else if (pjInfo->GetState() == STATE_UPDATED)
        {
            PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_SAVE_ACCOUNT_BATTLE_PET_LIST);
            stmt->setUInt32(0, GetSession()->GetAccountId());
            stmt->setUInt64(1, pet->first);
            stmt->setString(2, pjInfo->GetCustomName());
            stmt->setUInt32(3, pjInfo->GetCreatureEntry());
            stmt->setInt32(4, pjInfo->GetSpeciesID());
            stmt->setInt32(5, pjInfo->GetSummonSpell());
            stmt->setUInt8(6, pjInfo->GetLevel());
            stmt->setInt32(7, pjInfo->GetDisplayID());
            stmt->setUInt16(8, pjInfo->GetPower());
            stmt->setUInt16(9, pjInfo->GetSpeed());
            stmt->setInt32(10, pjInfo->GetHealth());
            stmt->setInt32(11, pjInfo->GetMaxHealth());
            stmt->setUInt8(12, pjInfo->GetQuality());
            stmt->setUInt16(13, pjInfo->GetXP());
            stmt->setUInt16(14, pjInfo->GetFlags());
            stmt->setInt16(15, pjInfo->GetBreedID());

            trans->Append(stmt);

            pjInfo->SetState(STATE_NORMAL);
        }
    }
}

void Player::_SaveBattlePetSlots(SQLTransaction& trans)
{
    // save slots
    PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_SAVE_ACCOUNT_BATTLE_PET_SLOTS);
    stmt->setUInt32(0, GetSession()->GetAccountId());
    for (int i = 0; i < 3; ++i)
    {
        PetBattleSlot * slot = GetBattlePetMgr()->GetPetBattleSlot(i);
        stmt->setUInt64(i+1, slot ? slot->GetPet() : 0);
    }
    trans->Append(stmt);
}

// save player stats -- only for external usage
// real stats will be recalculated on player login
void Player::_SaveStats(SQLTransaction& trans)
{
    // check if stat saving is enabled and if char level is high enough
    if (!sWorld->getIntConfig(CONFIG_MIN_LEVEL_STAT_SAVE) || getLevel() < sWorld->getIntConfig(CONFIG_MIN_LEVEL_STAT_SAVE))
        return;

    PreparedStatement* stmt = NULL;

    stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_CHAR_STATS);
    stmt->setUInt32(0, GetGUIDLow());
    trans->Append(stmt);

    uint8 index = 0;

    stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_CHAR_STATS);
    stmt->setUInt32(index++, GetGUIDLow());
    stmt->setUInt32(index++, GetMaxHealth());

    for (uint8 i = 0; i < MAX_POWERS_PER_CLASS; ++i)
        stmt->setUInt32(index++, GetMaxPower(Powers(i)));

    for (uint8 i = 0; i < MAX_STATS; ++i)
        stmt->setUInt32(index++, GetStat(Stats(i)));

    for (int i = 0; i < MAX_SPELL_SCHOOL; ++i)
        stmt->setUInt32(index++, GetResistance(SpellSchools(i)));

    stmt->setFloat(index++, GetFloatValue(PLAYER_BLOCK_PERCENTAGE));
    stmt->setFloat(index++, GetFloatValue(PLAYER_DODGE_PERCENTAGE));
    stmt->setFloat(index++, GetFloatValue(PLAYER_PARRY_PERCENTAGE));
    stmt->setFloat(index++, GetFloatValue(PLAYER_CRIT_PERCENTAGE));
    stmt->setFloat(index++, GetFloatValue(PLAYER_RANGED_CRIT_PERCENTAGE));
    stmt->setFloat(index++, GetFloatValue(PLAYER_SPELL_CRIT_PERCENTAGE1));
    stmt->setUInt32(index++, GetUInt32Value(UNIT_FIELD_ATTACK_POWER));
    stmt->setUInt32(index++, GetUInt32Value(UNIT_FIELD_RANGED_ATTACK_POWER));
    stmt->setUInt32(index++, GetBaseSpellPowerBonus());
    stmt->setUInt32(index++, GetUInt32Value(PLAYER_FIELD_COMBAT_RATING_1 + CR_RESILIENCE_PLAYER_DAMAGE_TAKEN));

    trans->Append(stmt);
}

void Player::_SaveBrackets(SQLTransaction& trans)
{
    for (uint8 i = 0; i < MAX_BRACKET_SLOT; ++i)
        if (Bracket* br = getBracket((BracketType)i))
            br->SaveStats(&trans);
}

