void Player::SendComboPoints()
{
    Unit* combotarget = ObjectAccessor::GetUnit(*this, m_comboTarget);
    if (combotarget)
    {
        WorldPacket data;
        ObjectGuid combotargetGuid = combotarget->GetGUID();
        /*if (m_mover != this)
        {
            data.Initialize(SMSG_PET_UPDATE_COMBO_POINTS, m_mover->GetPackGUID().size()+combotarget->GetPackGUID().size()+1);
            data.append(m_mover->GetPackGUID());
        }
        else*/
            //! 5.4.1
            data.Initialize(SMSG_UPDATE_COMBO_POINTS, combotarget->GetPackGUID().size()+1);
        data.WriteGuidMask<5, 4, 6, 1, 3, 2, 7, 0>(combotargetGuid);
        data.WriteGuidBytes<1, 3, 7>(combotargetGuid);
        data << uint8(m_comboPoints);
        data.WriteGuidBytes<6, 5, 2, 4, 0>(combotargetGuid);
        GetSession()->SendPacket(&data);
    }

    if (!m_comboPoints)
        m_comboTarget = 0;
}

void Player::AddComboPoints(Unit* target, int8 count, Spell* spell)
{
    if (!count)
        return;

    int8 oldComboPoints = m_comboPoints;
    bool needUpdateComboTarget = false;

    // without combo points lost (duration checked in aura)
    RemoveAurasByType(SPELL_AURA_RETAIN_COMBO_POINTS);

    if (target->GetGUID() == m_comboTarget)
    {
        if (spell)
            if (SpellInfo const* spellInfo = spell->GetSpellInfo())
                if (spellInfo->HasAura(SPELL_AURA_RETAIN_COMBO_POINTS))
                    count = int8(std::min(int8(5 - m_comboPoints), int8(count)));
    }
    else
    {
        if (m_comboTarget)
            if (Unit* target2 = ObjectAccessor::GetUnit(*this, m_comboTarget))
                target2->RemoveComboPointHolder(GetGUIDLow());

        // Spells will always add value to m_comboPoints eventualy, so it must be cleared first
        if (spell)
            m_comboPoints = 0;

        m_comboTarget = target->GetGUID();
        target->AddComboPointHolder(GetGUIDLow());
        needUpdateComboTarget = true;
    }

    m_comboPoints += count;

    if (m_comboPoints > 5)
    {
        if (HasAura(114015))
        {
            uint8 addStack = m_comboPoints - 5;

            if (Aura* aura = GetAura(115189))
            {
                if (aura->GetStackAmount() < 5)
                    aura->SetStackAmount(uint8(std::min(aura->GetStackAmount() + addStack, 5)));

                aura->SetDuration(aura->GetMaxDuration());
            }
            else
                AddAura(115189, this, NULL, addStack);
        }
        m_comboPoints = 5;
    }
    else if (m_comboPoints < 0)
        m_comboPoints = 0;

    if ((oldComboPoints != m_comboPoints || needUpdateComboTarget) && !m_updateComboPointsTimer)
        m_updateComboPointsTimer = 300;
}

void Player::ClearComboPoints(bool clearComboTarget)
{
    if (!m_comboTarget)
        return;

    // without combopoints lost (duration checked in aura)
    RemoveAurasByType(SPELL_AURA_RETAIN_COMBO_POINTS);

    // omg hack
    int32 chancekd = 0;
    if (HasAura(79096))
        chancekd = -2000 * GetComboPointsForDuration(m_comboPoints);

    m_comboPoints = 0;

    if (chancekd != 0)
    {
        ModifySpellCooldown(51690, chancekd);  // Killing Spree
        ModifySpellCooldown(13750, chancekd);  // Adrenaline Rush
        ModifySpellCooldown(2983, chancekd);   // Sprint
        ModifySpellCooldown(121471, chancekd); // Shadow Blades
    }

    if (Unit* target = ObjectAccessor::GetUnit(*this, m_comboTarget))
        target->RemoveComboPointHolder(GetGUIDLow());

    if (clearComboTarget)
        SendComboPoints();
    else
        m_updateComboPointsTimer = 300;
}

void Player::SetGroup(Group* group, int8 subgroup)
{
    if (group == NULL)
        m_group.unlink();
    else
    {
        // never use SetGroup without a subgroup unless you specify NULL for group
        ASSERT(subgroup >= 0);
        m_group.link(group, this);
        m_group.setSubGroup((uint8)subgroup);
    }

    UpdateObjectVisibility(false);
}

//! Before send self obj. update 
void Player::SendInitialPacketsBeforeAddToMap()
{
    /// Pass 'this' as argument because we're not stored in ObjectAccessor yet
    GetSocial()->SendSocialList(this);

    // guild bank list wtf?
    WorldPacket data(SMSG_BATTLE_PET_JOURNAL_LOCK_DENIED);
    GetSession()->SendPacket(&data);

    // Homebind
    data.Initialize(SMSG_BINDPOINTUPDATE, 20);
    data << m_homebindZ;
    data << m_homebindY;
    data << uint32(m_homebindMapId);
    data << m_homebindX;
    data << uint32(m_homebindAreaId);
    GetSession()->SendPacket(&data);

    // SMSG_SET_PROFICIENCY
    SendSpellMods();
    // SMSG_UPDATE_AURA_DURATION

    data.Initialize(SMSG_SPELL_0x00E9);
    GetSession()->SendPacket(&data);

    SendTalentsInfoData(false);

    data.Initialize(SMSG_SEND_UNLEARN_SPELLS);
    data.WriteBits(0, 22);                                  // count, read uint32 spells id
    data.FlushBits();
    GetSession()->SendPacket(&data);

    data.Initialize(SMSG_SPELL_0x00E9);
    GetSession()->SendPacket(&data);

    SendKnownSpells();
    SendSpellHistoryData();
    SendSpellChargeData();

    SendInitialActionButtons();

    data.Initialize(SMSG_CORPSE_RECLAIM_DELAY, 1);
    data.WriteBit(1);
    data.FlushBits();
    GetSession()->SendPacket(&data);

    //SMSG_ALL_ACHIEVEMENT_DATA first send
    m_achievementMgr.SendAllAccountCriteriaData(this);
    m_reputationMgr.SendInitialReputations();
    
    ResetTimeSync();
    SendTimeSync();

    //SMSG_WEATHER done on UpdateZone
    SendCurrencies();

    // Reset Vignitte data
    data.Initialize(SMSG_CLIENT_VIGNETTE_DATA, 15);
    data.WriteBits(0, 24);
    data.WriteBits(0, 24);
    data.WriteBit(1);
    data.WriteBits(0, 20);
    data.WriteBits(0, 20);
    data.WriteBits(0, 24);
    GetSession()->SendPacket(&data);

    SendEquipmentSetList();
    m_achievementMgr.SendAllAchievementData(this);  //second send

    data.Initialize(SMSG_SUSPEND_TOKEN_RESPONSE, 15);
    data << uint32(0);
    data.WriteBits(2, 2);
    GetSession()->SendPacket(&data);

    data.Initialize(SMSG_LOGIN_VERIFY_WORLD, 20);
    data << GetPositionZ();
    data << GetPositionY();
    data << GetMapId();
    data << GetOrientation();
    data << GetPositionX();
    GetSession()->SendPacket(&data);

    data.Initialize(SMSG_LOGIN_SETTIMESPEED, 4 + 4 + 4);
    data << uint32(0);
    data << uint32(0);
    data << float(0.01666667f);                             // game speed
    data.AppendPackedTime(sWorld->GetGameTime());
    data.AppendPackedTime(sWorld->GetGameTime());
    GetSession()->SendPacket(&data);
    GetReputationMgr().SendForceReactions();                // SMSG_SET_FORCED_REACTIONS

    // SMSG_UPDATE_WORLD_STATE
    // SMSG_SET_PHASE_SHIFT
    // SMSG_UPDATE_CURRENCY_WEEK_LIMIT
    // SMSG_WEEKLY_SPELL_USAGE

    // SMSG_0x010E
    // SMSG_ACCOUNT_CRITERIA_UPDATE
    SendPvpRatedStats();

    // First obj create should be with dynamic data
    if (GetSkillValue(SKILL_ARCHAEOLOGY) && sWorld->getBoolConfig(CONFIG_ARCHAEOLOGY_ENABLED))
        ShowResearchSites();
}

//! After send self obj. update 
void Player::SendInitialPacketsAfterAddToMap()
{
    UpdateVisibilityForPlayer();

    // update zone
    uint32 newzone, newarea;
    GetZoneAndAreaId(newzone, newarea);
    UpdateZone(newzone, newarea);                            // also call SendInitWorldStates();

    InstanceMap* inst = GetMap()->ToInstanceMap();
    uint32 instancePlayers = inst ? inst->GetMaxPlayers() : 0;

    WorldPacket data(SMSG_WORLD_SERVER_INFO, 4 + 4 + 1 + 1);
    data << uint32(sWorld->GetNextWeeklyQuestsResetTime() -  WEEK);
    data << uint8(0);                                       // is on tournament realm
    data << uint32(GetMap()->GetDifficulty());
    data.WriteBit(instancePlayers);                         // has instance players count
    data.WriteBit(false);                                   // is ineligible for loot
    data.WriteBit(false);                                   // has trial money
    data.WriteBit(false);                                   // has trial level
    if (instancePlayers)
        data << uint32(instancePlayers);
    GetSession()->SendPacket(&data);

    // SMSG_TALENTS_INFO x 2 for pet (unspent points and talents in separate packets...)
    /* 
        smthng like this...

        WorldPacket data(SMSG_PET_GUIDS);
        data.WriteBits(m_petlist.size(), 24);
        for (auto itr : m_petlist)
        {
            data.WriteGuidMask<4, 7, 6, 1, 0, 2, 3, 5>(itr->second.guid);
            data.WriteGuidBytes<4, 2, 6, 5, 3, 0, 1, 7>(itr->second.guid);
        }
        GetSession()->SendPacket(&data);
    */

    // SMSG_POWER_UPDATE

    CastSpell(this, 836, true);                             // LOGINEFFECT

    // set some aura effects that send packet to player client after add player to map
    // SendMessageToSet not send it to player not it map, only for aura that not changed anything at re-apply
    // same auras state lost at far teleport, send it one more time in this case also
    static const AuraType auratypes[] =
    {
        SPELL_AURA_MOD_FEAR,     SPELL_AURA_TRANSFORM,                 SPELL_AURA_WATER_WALK,
        SPELL_AURA_FEATHER_FALL, SPELL_AURA_HOVER,                     SPELL_AURA_SAFE_FALL,
        SPELL_AURA_FLY,          SPELL_AURA_MOD_INCREASE_MOUNTED_FLIGHT_SPEED, SPELL_AURA_NONE
    };
    for (AuraType const* itr = &auratypes[0]; itr && itr[0] != SPELL_AURA_NONE; ++itr)
    {
        Unit::AuraEffectList const& auraList = GetAuraEffectsByType(*itr);
        if (!auraList.empty())
            auraList.front()->HandleEffect(this, AURA_EFFECT_HANDLE_SEND_FOR_CLIENT, true);
    }

    if (HasAuraType(SPELL_AURA_MOD_STUN))
        SetRooted(true);

    // manual send package (have code in HandleEffect(this, AURA_EFFECT_HANDLE_SEND_FOR_CLIENT, true); that must not be re-applied.
    if (HasAuraType(SPELL_AURA_MOD_ROOT))
        SendMoveRoot(2);

    SendAurasForTarget(this);
    SendEnchantmentDurations();                             // must be after add to map
    SendItemDurations();                                    // must be after add to map

    // raid downscaling - send difficulty to player
    if (GetMap()->IsRaid())
    {
        if (GetMap()->GetDifficulty() != GetRaidDifficulty())
        {
            StoreRaidMapDifficulty();
            SendRaidDifficulty(GetStoredRaidDifficulty());
        }
    }
    else if (GetRaidDifficulty() != GetStoredRaidDifficulty())
        SendRaidDifficulty();

    if (GetBattlePetMgr()->BuildPetJournal(&data))
        GetSession()->SendPacket(&data);
    else
        GetBattlePetMgr()->SendEmptyPetJournal();

    // send timers if already start challenge for example
    SendInitWorldTimers();

    if (getClass() == CLASS_DEATH_KNIGHT)
    {
        RestoreAllBaseRunes();
        SetPower(POWER_RUNIC_POWER, 0);
    }
    GetSession()->SendStablePet(0);

    // send step data when entering scenarios
    if (uint32 instanceId = inst ? inst->GetInstanceId() : 0)
        if (ScenarioProgress* progress = sScenarioMgr->GetScenarioProgress(instanceId))
            progress->SendStepUpdate(this, true);

    SetMover(this);
}

void Player::SendSpellHistoryData()
{
    time_t curTime = time(NULL);
    time_t infTime = curTime + infinityCooldownDelayCheck;
    uint32 spellCount = 0;

    ByteBuffer buff;
    WorldPacket data(SMSG_SPELL_HISTORY_DATA);
    size_t count_pos = data.bitwpos();
    data.WriteBits(0, 19);

    for (SpellCooldowns::const_iterator itr = m_spellCooldowns.begin(); itr != m_spellCooldowns.end(); ++itr)
    {
        SpellInfo const *info = sSpellMgr->GetSpellInfo(itr->first);

        if(!info)
            continue;

        bool onHold = itr->second.end >= infTime;

        data.WriteBit(onHold);                              // onHold
        buff << uint32(itr->second.itemid);                 // ItemID
        buff << uint32(info->Category);                     // Category

        // send infinity cooldown in special format
        if (onHold)
        {
            buff << uint32(1);                              // RecoveryTime
            buff << uint32(itr->first);                     // SpellID
            buff << uint32(0);                              // CategoryRecoveryTime
            continue;
        }

        time_t cooldown = itr->second.end > curTime ? (itr->second.end - curTime) * IN_MILLISECONDS : 0;

        buff << uint32(cooldown);                       // RecoveryTime
        buff << uint32(itr->first);                     // SpellID
        if (info->Category)
            buff << uint32(cooldown);                   // CategoryRecoveryTime
        else
            buff << uint32(0);

        ++spellCount;
    }

    data.FlushBits();

    if (!buff.empty())
        data.append(buff);

    data.PutBits(count_pos, spellCount, 19);

    GetSession()->SendPacket(&data);
}

void Player::SendSpellChargeData()
{
    WorldPacket data(SMSG_SPELL_CHARGE_DATA, m_spellChargeData.size() * 9 + 3);
    data.WriteBits(m_spellChargeData.size(), 21);
    for (SpellChargeDataMap::const_iterator itr = m_spellChargeData.begin(); itr != m_spellChargeData.end(); ++itr)
    {
        SpellChargeData const& chargeData = itr->second;
        data << uint8(chargeData.maxCharges - chargeData.charges);  // ConsumedCharges (on cd)
        int32 diff = int32(chargeData.categoryEntry->chargeRegenTime) - int32(chargeData.timer);
        if (diff < 0)
            diff = 0;
        data << uint32(chargeData.charges != chargeData.maxCharges ? diff : 0); // NextRecoveryTime
        data << uint32(itr->first);             // Category
    }

    SendDirectMessage(&data);
}

void Player::SendUpdateToOutOfRangeGroupMembers()
{
    if (m_groupUpdateMask == GROUP_UPDATE_FLAG_NONE)
        return;
    if (Group* group = GetGroup())
        group->UpdatePlayerOutOfRange(this);

    m_groupUpdateMask = GROUP_UPDATE_FLAG_NONE;
    m_auraRaidUpdateMask = 0;
    if (Pet* pet = GetPet())
        pet->ResetAuraUpdateMaskForRaid();
}

void Player::SendTransferAborted(uint32 mapid, TransferAbortReason reason, uint8 arg)
{
    TC_LOG_DEBUG("network", "Player::SendTransferAborted  mapid %i, reason %i, arg %i", mapid, reason, arg);
    //! 5.4.1
    WorldPacket data(SMSG_TRANSFER_ABORTED, 4+2);
    data.WriteBits(reason, 5);      // transfer abort reason
    data.WriteBit(!arg);
    data.FlushBits();
    if (arg)
        data << uint8(arg);
    data << uint32(mapid);
    GetSession()->SendPacket(&data);
}

void Player::SendInstanceResetWarning(uint32 mapid, Difficulty difficulty, uint32 time)
{
    // type of warning, based on the time remaining until reset
    uint32 type;
    if (time > 3600)
        type = RAID_INSTANCE_WELCOME;
    else if (time > 900 && time <= 3600)
        type = RAID_INSTANCE_WARNING_HOURS;
    else if (time > 300 && time <= 900)
        type = RAID_INSTANCE_WARNING_MIN;
    else
        type = RAID_INSTANCE_WARNING_MIN_SOON;

    WorldPacket data(SMSG_RAID_INSTANCE_MESSAGE, 4 + 1 + 4 + 4 + 1);
    data << uint32(difficulty);
    data << uint8(type);
    data << uint32(time);
    data << uint32(mapid);
    data.WriteBit(0);                                   // is locked or next
    data.WriteBit(0);                                   // is extended, ignored if prev field is 0 or prev
    GetSession()->SendPacket(&data);
}

void Player::ApplyEquipCooldown(Item* pItem)
{
    if (pItem->HasFlag(ITEM_FIELD_FLAGS, ITEM_FLAG_NO_EQUIP_COOLDOWN))
        return;

    for (uint8 i = 0; i < MAX_ITEM_PROTO_SPELLS; ++i)
    {
        _Spell const& spellData = pItem->GetTemplate()->Spells[i];

        // no spell
        if (!spellData.SpellId)
            continue;

        // wrong triggering type (note: ITEM_SPELLTRIGGER_ON_NO_DELAY_USE not have cooldown)
        if (spellData.SpellTrigger != ITEM_SPELLTRIGGER_ON_USE)
            continue;

        AddSpellCooldown(spellData.SpellId, pItem->GetEntry(), getPreciseTime() + 30.0);

        //! 5.4.1
        WorldPacket data(SMSG_ITEM_COOLDOWN, 12);
        data << pItem->GetGUID();
        data << uint32(spellData.SpellId);
        GetSession()->SendPacket(&data);
    }
}

void Player::resetSpells(bool myClassOnly)
{
    // not need after this call
    if (HasAtLoginFlag(AT_LOGIN_RESET_SPELLS))
        RemoveAtLoginFlag(AT_LOGIN_RESET_SPELLS, true);

    // make full copy of map (spells removed and marked as deleted at another spell remove
    // and we can't use original map for safe iterative with visit each spell at loop end
    PlayerSpellMap smap = GetSpellMap();

    uint32 family;

    if (myClassOnly)
    {
        ChrClassesEntry const* clsEntry = sChrClassesStore.LookupEntry(getClass());
        if (!clsEntry)
            return;
        family = clsEntry->spellfamily;

        for (PlayerSpellMap::const_iterator iter = smap.begin(); iter != smap.end(); ++iter)
        {
            SpellInfo const* spellInfo = sSpellMgr->GetSpellInfo(iter->first);
            if (!spellInfo)
                continue;

            // skip server-side/triggered spells
            if (spellInfo->SpellLevel == 0)
                continue;

            // skip wrong class/race skills
            if (!IsSpellFitByClassAndRace(spellInfo->Id))
                continue;

            // skip other spell families
            if (spellInfo->SpellFamilyName != family)
                continue;

            // skip spells with first rank learned as talent (and all talents then also)
            uint32 first_rank = sSpellMgr->GetFirstSpellInChain(spellInfo->Id);
            /*if (GetTalentSpellCost(first_rank) > 0)
                continue;*/

            // skip broken spells
            if (!SpellMgr::IsSpellValid(spellInfo, this, false))
                continue;
        }
    }
    else
        for (PlayerSpellMap::const_iterator iter = smap.begin(); iter != smap.end(); ++iter)
            removeSpell(iter->first, false, false);           // only iter->first can be accessed, object by iter->second can be deleted already

    learnDefaultSpells();
    learnQuestRewardedSpells();
}

void Player::learnDefaultSpells()
{
    // learn default race/class spells
    PlayerInfo const* info = sObjectMgr->GetPlayerInfo(getRace(), getClass());
    for (PlayerCreateInfoSpells::const_iterator itr = info->spell.begin(); itr != info->spell.end(); ++itr)
    {
        uint32 tspell = *itr;
        TC_LOG_DEBUG("player.loading", "PLAYER (Class: %u Race: %u): Adding initial spell, id = %u", uint32(getClass()), uint32(getRace()), tspell);
        if (!IsInWorld())                                    // will send in INITIAL_SPELLS in list anyway at map add
            addSpell(tspell, true, true, true, false);
        else                                                // but send in normal spell in game learn case
            learnSpell(tspell, true);
    }

    if (getRace() == RACE_WORGEN && GetMapId() != 654)
    {
        learnSpell(68975, true);
        learnSpell(68976, true);
        learnSpell(68978, true);
        learnSpell(68992, true);
        learnSpell(68996, true);
        learnSpell(94293, true);
    }
}

void Player::learnQuestRewardedSpells(Quest const* quest)
{
    int32 spell_id = quest->GetRewSpellCast();
    uint32 src_spell_id = quest->GetSrcSpell();

    // skip quests without rewarded spell
    if (!spell_id)
        return;

    // if RewSpellCast = -1 we remove aura do to SrcSpell from player.
    if (spell_id == -1 && src_spell_id)
    {
        RemoveAurasDueToSpell(src_spell_id);
        return;
    }

    SpellInfo const* spellInfo = sSpellMgr->GetSpellInfo(spell_id);
    if (!spellInfo)
        return;

    // check learned spells state
    bool found = false;
    for (uint8 i = 0; i < MAX_SPELL_EFFECTS; ++i)
    {
        if (spellInfo->Effects[i]->Effect == SPELL_EFFECT_LEARN_SPELL && !HasSpell(spellInfo->Effects[i]->TriggerSpell))
        {
            found = true;
            break;
        }
    }

    // skip quests with not teaching spell or already known spell
    if (!found)
        return;

    // prevent learn non first rank unknown profession and second specialization for same profession)
    uint32 learned_0 = spellInfo->Effects[0]->TriggerSpell;
    if (sSpellMgr->GetSpellRank(learned_0) > 1 && !HasSpell(learned_0))
    {
        // not have first rank learned (unlearned prof?)
        uint32 first_spell = sSpellMgr->GetFirstSpellInChain(learned_0);
        if (!HasSpell(first_spell))
            return;

        SpellInfo const* learnedInfo = sSpellMgr->GetSpellInfo(learned_0);
        if (!learnedInfo)
            return;

        SpellsRequiringSpellMapBounds spellsRequired = sSpellMgr->GetSpellsRequiredForSpellBounds(learned_0);
        for (SpellsRequiringSpellMap::const_iterator itr2 = spellsRequired.first; itr2 != spellsRequired.second; ++itr2)
        {
            uint32 profSpell = itr2->second;

            // specialization
            if (learnedInfo->Effects[0]->Effect == SPELL_EFFECT_TRADE_SKILL && learnedInfo->Effects[1]->Effect == 0 && profSpell)
            {
                PlayerSpellMap _spells = m_spells;
                // search other specialization for same prof
                for (PlayerSpellMap::const_iterator itr = _spells.begin(); itr != _spells.end(); ++itr)
                {
                    if (itr->second->state == PLAYERSPELL_REMOVED || itr->first == learned_0)
                        continue;

                    SpellInfo const* itrInfo = sSpellMgr->GetSpellInfo(itr->first);
                    if (!itrInfo)
                        return;

                    // compare only specializations
                    if (itrInfo->Effects[0]->Effect != SPELL_EFFECT_TRADE_SKILL || itrInfo->Effects[1]->Effect != 0)
                        continue;

                    // compare same chain spells
                    if (sSpellMgr->IsSpellRequiringSpell(itr->first, profSpell))
                        return;
                }
            }
        }
    }

    // skill requeare
    SkillLineAbilityMapBounds bounds = sSpellMgr->GetSkillLineAbilityMapBounds(learned_0);
    for (SkillLineAbilityMap::const_iterator _spell_idx = bounds.first; _spell_idx != bounds.second; ++_spell_idx)
    {
        SkillLineEntry const* pSkill = sSkillLineStore.LookupEntry(_spell_idx->second->skillId);
        if (!pSkill)
            continue;

        if (!HasSkill(_spell_idx->second->skillId))
            return;
    }

    CastSpell(this, spell_id, true);
}

void Player::learnQuestRewardedSpells()
{
    // learn spells received from quest completing
    for (RewardedQuestSet::const_iterator itr = m_RewardedQuests.begin(); itr != m_RewardedQuests.end(); ++itr)
    {
        Quest const* quest = sObjectMgr->GetQuestTemplate(*itr);
        if (!quest)
            continue;

        learnQuestRewardedSpells(quest);
    }
}

void Player::learnSkillRewardedSpells(uint32 skill_id, uint32 skill_value)
{
    uint32 raceMask  = getRaceMask();
    uint32 classMask = getClassMask();
    for (uint32 j=0; j<sSkillLineAbilityStore.GetNumRows(); ++j)
    {
        SkillLineAbilityEntry const* pAbility = sSkillLineAbilityStore.LookupEntry(j);
        if (!pAbility || pAbility->skillId != skill_id || pAbility->learnOnGetSkill != ABILITY_LEARNED_ON_GET_PROFESSION_SKILL)
            continue;
        // Check race if set
        if (pAbility->racemask && !(pAbility->racemask & raceMask))
            continue;
        // Check class if set
        if (pAbility->classmask && !(pAbility->classmask & classMask))
            continue;

        if (sSpellMgr->GetSpellInfo(pAbility->spellId))
        {
            // need unlearn spell
            if (skill_value < pAbility->req_skill_value)
                removeSpell(pAbility->spellId);
            // need learn
            else if (!IsInWorld())
                addSpell(pAbility->spellId, true, true, true, false);
            else
                learnSpell(pAbility->spellId, true);
        }
    }
}

void Player::SendAurasForTarget(Unit* target)
{
    if (!target || target->GetVisibleAuras()->empty())                  // speedup things
        return;

    /*! Blizz sends certain movement packets sometimes even before CreateObject
        These movement packets are usually found in SMSG_COMPRESSED_MOVES
    */
    // NOT SURE...but for mobs?
    if (target->HasAuraType(SPELL_AURA_FEATHER_FALL))
        target->SetFeatherFall(true, true);

    if (target->HasAuraType(SPELL_AURA_WATER_WALK))
    {
        if (target->GetTypeId() == TYPEID_PLAYER)
        {
            if (Player* plr = target->ToPlayer())
                if (!plr->GetAnticheatMgr()->HasState(PLAYER_STATE_WATER_WALK))
                    plr->GetAnticheatMgr()->AddState(PLAYER_STATE_WATER_WALK);
        }

        target->SetWaterWalking(true, true);
    }

    if (target->HasAuraType(SPELL_AURA_HOVER))
    {
        if (target->GetTypeId() == TYPEID_PLAYER)
        {
            if (Player* plr = target->ToPlayer())
                if (!plr->GetAnticheatMgr()->HasState(PLAYER_STATE_HOVER))
                    plr->GetAnticheatMgr()->AddState(PLAYER_STATE_HOVER);
        }

        target->SetHover(true, true);
    }

    bool flyAuras = (target->HasAuraType(SPELL_AURA_FLY) || target->HasAuraType(SPELL_AURA_MOD_INCREASE_VEHICLE_FLIGHT_SPEED)
        || target->HasAuraType(SPELL_AURA_MOD_INCREASE_MOUNTED_FLIGHT_SPEED) || target->HasAuraType(SPELL_AURA_MOD_INCREASE_FLIGHT_SPEED)
        || target->HasAuraType(SPELL_AURA_MOD_MOUNTED_FLIGHT_SPEED_ALWAYS));

    if (flyAuras)
    {
        if (target->GetTypeId() == TYPEID_PLAYER)
        {
            if (Player* plr = target->ToPlayer())
                if (!plr->GetAnticheatMgr()->HasState(PLAYER_STATE_CAN_FLY))
                    plr->GetAnticheatMgr()->AddState(PLAYER_STATE_CAN_FLY);
        }
    }

    ObjectGuid targetGuid = target->GetObjectGuid();
    Unit::VisibleAuraMap const* visibleAuras = target->GetVisibleAuras();

    WorldPacket data(SMSG_AURA_UPDATE);
    data.WriteGuidMask<0>(targetGuid);
    data.WriteBit(0);   // has power unit
    data.WriteBit(1);   // full update
    data.WriteGuidMask<6>(targetGuid);
    /*
    if (hasPowerData) { }
    */
    data.WriteGuidMask<4, 7, 3>(targetGuid);
    data.WriteBits(visibleAuras->size(), 24);
    data.WriteGuidMask<1, 5, 2>(targetGuid);

    for (Unit::VisibleAuraMap::const_iterator itr = visibleAuras->begin(); itr != visibleAuras->end(); ++itr)
        itr->second->BuildBitUpdatePacket(data, false);

    for (Unit::VisibleAuraMap::const_iterator itr = visibleAuras->begin(); itr != visibleAuras->end(); ++itr)
        itr->second->BuildByteUpdatePacket(data, false);
    /*
    if (hasPowerData) { }
    */

    data.WriteGuidBytes<7, 4, 2, 0, 6, 5, 1, 3>(targetGuid);

    GetSession()->SendPacket(&data);
}

void Player::SetDailyQuestStatus(uint32 quest_id)
{
    if (Quest const* qQuest = sObjectMgr->GetQuestTemplate(quest_id))
    {
        if (!qQuest->IsDFQuest())
        {
            if (m_dailyquests.find(quest_id) == m_dailyquests.end())
            {
                m_dailyquests.insert(quest_id);
                m_lastDailyQuestTime = time(NULL);              // last daily quest time
                m_DailyQuestChanged = true;
            }
        }
        else
        {
            m_DFQuests.insert(quest_id);
            m_lastDailyQuestTime = time(NULL);
            m_DailyQuestChanged = true;
        }
    }
}

void Player::SetWeeklyQuestStatus(uint32 quest_id)
{
    m_weeklyquests.insert(quest_id);
    m_WeeklyQuestChanged = true;
}

void Player::SetSeasonalQuestStatus(uint32 quest_id)
{
    Quest const* quest = sObjectMgr->GetQuestTemplate(quest_id);
    if (!quest)
        return;

    m_seasonalquests[sGameEventMgr->GetEventIdForQuest(quest)].insert(quest_id);
    m_SeasonalQuestChanged = true;
}

void Player::ResetDailyQuestStatus()
{
    m_dailyquests.clear();
    m_DFQuests.clear(); // Dungeon Finder Quests.

    // DB data deleted in caller
    m_DailyQuestChanged = false;
    m_lastDailyQuestTime = 0;
}

void Player::ResetWeeklyQuestStatus()
{
    if (m_weeklyquests.empty())
        return;

    m_weeklyquests.clear();
    // DB data deleted in caller
    m_WeeklyQuestChanged = false;

}

void Player::ResetSeasonalQuestStatus(uint16 event_id)
{
    if (m_seasonalquests.empty() || m_seasonalquests[event_id].empty())
        return;

    m_seasonalquests.erase(event_id);
    // DB data deleted in caller
    m_SeasonalQuestChanged = false;
}

