void Player::RestoreAllBaseRunes()
{
    for (uint8 i = 0; i < MAX_RUNES; ++i)
    {
        if (!IsBlockedRuneConvert(i))
            RestoreBaseRune(i);
        else
            ConvertRune(i, GetCurrentRune(i));
    }
}

void Player::RestoreBaseRune(uint8 index)
{
    ConvertRune(index, GetBaseRune(index));
    SetConvertIn(index, GetBaseRune(index));
}

void Player::ConvertRune(uint8 index, RuneType newType)
{
    SetCurrentRune(index, newType);

    WorldPacket data(SMSG_CONVERT_RUNE, 2);
    data << uint8(newType);
    data << uint8(index);
    GetSession()->SendPacket(&data);
}

void Player::ResyncRunes(uint8 count)
{
    WorldPacket data(SMSG_RESYNC_RUNES, 3 + count * 2);
    data.WriteBits(count, 23);
    for (uint32 i = 0; i < count; ++i)
    {
        data << uint8(GetCurrentRune(i));                   // rune type
        data << uint8(255 - (GetRuneCooldown(i) * 51));     // passed cooldown time (0-255)
    }
    GetSession()->SendPacket(&data);
}

void Player::SendDeathRuneUpdate(uint8 index)
{
    RuneType rune = GetBaseRune(index);
    WorldPacket data(SMSG_CONVERT_RUNE, 2);
    data << uint8(rune);
    data << uint8(index);
    GetSession()->SendPacket(&data);

    WorldPacket data1(SMSG_CONVERT_RUNE, 2);
    data1 << uint8(RUNE_DEATH);
    data1 << uint8(index);
    GetSession()->SendPacket(&data1);
}

void Player::AddRunePower(uint8 index)
{
    WorldPacket data(SMSG_ADD_RUNE_POWER, 4);
    data << uint32(1 << index);                             // mask (0x00-0x3F probably)
    GetSession()->SendPacket(&data);
}

static RuneType runeSlotTypes[MAX_RUNES] =
{
    /*0*/ RUNE_BLOOD,
    /*1*/ RUNE_BLOOD,
    /*2*/ RUNE_UNHOLY,
    /*3*/ RUNE_UNHOLY,
    /*4*/ RUNE_FROST,
    /*5*/ RUNE_FROST
};

void Player::InitRunes()
{
    if (getClass() != CLASS_DEATH_KNIGHT)
        return;

    m_runes.runeState = 0;

    for (uint8 i = 0; i < MAX_RUNES; ++i)
    {
        SetBaseRune(i, runeSlotTypes[i]);                              // init base types
        SetCurrentRune(i, runeSlotTypes[i]);                           // init current types
        SetRuneCooldown(i, 0);                                         // reset cooldowns
        m_runes.SetRuneState(i);
        SetDeathRuneUsed(i, false);
        SetConvertIn(i, runeSlotTypes[i]);
        SetBlockRuneConvert(i, false);
    }

    for (uint8 i = 0; i < NUM_RUNE_TYPES; ++i)
        SetFloatValue(PLAYER_RUNE_REGEN_1 + i, 0.1f);                  // set a base regen timer equal to 10 sec
}

bool Player::IsBaseRuneSlotsOnCooldown(RuneType runeType) const
{
    for (uint8 i = 0; i < MAX_RUNES; ++i)
        if (GetBaseRune(i) == runeType && GetRuneCooldown(i) == 0)
            return false;

    return true;
}

bool Player::AutoStoreLoot(uint8 bag, uint8 slot, uint32 loot_id, LootStore const& store, uint32 filterLevel, bool broadcast)
{
    Loot loot;
    loot.objType = 4;
    if(filterLevel > 399)
        loot.itemLevel = filterLevel;
    loot.FillLoot(loot_id, store, this, true);

    uint32 max_slot = loot.GetMaxSlotInLootFor(this);
    for (uint32 i = 0; i < max_slot; ++i)
    {
        LootItem* lootItem = loot.LootItemInSlot(i, this);
        if (!lootItem->AllowedForPlayer(this))
            continue;

        if (lootItem->currency)
        {
            uint32 id = lootItem->itemid;
            CurrencyTypesEntry const* entry = sCurrencyTypesStore.LookupEntry(id);
            if (!id)
                continue;

            uint32 amount = lootItem->count * entry->GetPrecision();

            if (uint32 totalCap = GetCurrencyTotalCap(entry))
            {
                if (GetCurrency(id) + amount > totalCap)
                {
                    SendEquipError(EQUIP_ERR_ITEM_MAX_COUNT, NULL);
                    continue;
                }
            }

            ModifyCurrency(id, amount, true);
        }
        else
        {
            ItemPosCountVec dest;
            InventoryResult msg = CanStoreNewItem(bag, slot, dest, lootItem->itemid, lootItem->count);
            if (msg != EQUIP_ERR_OK && slot != NULL_SLOT)
                msg = CanStoreNewItem(bag, NULL_SLOT, dest, lootItem->itemid, lootItem->count);
            if (msg != EQUIP_ERR_OK && bag != NULL_BAG)
                msg = CanStoreNewItem(NULL_BAG, NULL_SLOT, dest, lootItem->itemid, lootItem->count);
            if (msg != EQUIP_ERR_OK)
            {
                SendEquipError(msg, NULL, NULL, lootItem->itemid);
                continue;
            }

            Item* pItem = StoreNewItem(dest, lootItem->itemid, true, lootItem->randomPropertyId);
            SendNewItem(pItem, lootItem->count, false, false, broadcast);
        }
    }
    if (max_slot > 0)
        return true;

    return false;
}

void Player::StoreLootItem(uint8 lootSlot, Loot* loot)
{
    QuestItem* qitem = NULL;
    QuestItem* ffaitem = NULL;
    QuestItem* conditem = NULL;
    QuestItem* currency = NULL;

    LootItem* item = loot->LootItemInSlot(lootSlot, this, &qitem, &ffaitem, &conditem, &currency);

    if (!item)
    {
        SendEquipError(EQUIP_ERR_LOOT_GONE, NULL, NULL);
        return;
    }

    // questitems use the blocked field for other purposes
    if (!qitem && item->is_blocked)
    {
        SendLootRelease(GetLootGUID());
        return;
    }

    if (currency)
    {
        if (CurrencyTypesEntry const * currencyEntry = sCurrencyTypesStore.LookupEntry(item->itemid))
            ModifyCurrency(item->itemid, int32(item->count * currencyEntry->GetPrecision()));

        SendNotifyLootItemRemoved(lootSlot, loot);
        currency->is_looted = true;
        --loot->unlootedCount;
        if(loot->personal)
            SendDisplayToast(item->itemid, 3, 0/*loot->bonusLoot*/, item->count, 2);
        return;
    }

    //TC_LOG_DEBUG("loot", "Player::StoreLootItem itemid %i count %i", item->itemid, item->count);

    ItemPosCountVec dest;
    InventoryResult msg = CanStoreNewItem(NULL_BAG, NULL_SLOT, dest, item->itemid, item->count);
    if (msg == EQUIP_ERR_OK)
    {
        AllowedLooterSet looters = item->GetAllowedLooters();
        Item* newitem = StoreNewItem(dest, item->itemid, true, item->randomPropertyId, looters);

        if (qitem)
        {
            qitem->is_looted = true;
            //freeforall is 1 if everyone's supposed to get the quest item.
            if (item->freeforall || loot->GetPlayerQuestItems().size() == 1)
                SendNotifyLootItemRemoved(lootSlot, loot);
            else
                loot->NotifyQuestItemRemoved(qitem->index);
        }
        else
        {
            if (ffaitem)
            {
                //freeforall case, notify only one player of the removal
                ffaitem->is_looted = true;
                SendNotifyLootItemRemoved(lootSlot, loot);
            }
            else
            {
                //not freeforall, notify everyone
                if (conditem)
                    conditem->is_looted = true;
                loot->NotifyItemRemoved(lootSlot);
            }
        }

        //if only one person is supposed to loot the item, then set it to looted
        if (!item->freeforall)
            item->is_looted = true;

        --loot->unlootedCount;

        if (const ItemTemplate* proto = sObjectMgr->GetItemTemplate(item->itemid))
        {
            if (proto->Quality > ITEM_QUALITY_EPIC || (proto->Quality == ITEM_QUALITY_EPIC && proto->ItemLevel >= MinNewsItemLevel[sWorld->getIntConfig(CONFIG_EXPANSION)]))
                if (Guild* guild = sGuildMgr->GetGuildById(GetGuildId()))
                    guild->GetNewsLog().AddNewEvent(GUILD_NEWS_ITEM_LOOTED, time(NULL), GetGUID(), 0, item->itemid);

            if (loot->personal && proto->Quality >= uint32(ITEM_QUALITY_UNCOMMON))
                SendDisplayToast(item->itemid, 1, 0/*loot->bonusLoot*/, item->count, 1, newitem);
        }

        SendNewItem(newitem, uint32(item->count), false, false, true, NULL, loot->bonusLoot);
        UpdateAchievementCriteria(CRITERIA_TYPE_LOOT_ITEM, item->itemid, item->count);
        UpdateAchievementCriteria(CRITERIA_TYPE_LOOT_TYPE, loot->loot_type, item->count);
        UpdateAchievementCriteria(CRITERIA_TYPE_LOOT_EPIC_ITEM, item->itemid, item->count);
    }
    else
        SendEquipError(msg, NULL, NULL, item->itemid);
}

