// TODO : Check cheat-hack issue with packet-editing
bool Player::LearnTalent(uint32 talentId)
{
    uint32 CurTalentPoints = GetFreeTalentPoints();

    if (CurTalentPoints == 0)
        return false;

    TalentEntry const* talentInfo = sTalentStore.LookupEntry(talentId);

    if (!talentInfo)
        return false;

    if (talentInfo->classId != getClass())
        return false;

    uint32 spellid = talentInfo->spellId;
    if (spellid == 0)
    {
        TC_LOG_ERROR("player", "Talent.dbc have for talent: %uspell id = 0", talentId);
        return false;
    }

    // already known
    if (HasSpell(spellid))
        return false;

    // Fix bug WPE talent
    for (uint32 i = 0; i < sTalentStore.GetNumRows(); i++)              // Loop through all talents.
    {
        if (TalentEntry const* tmpTalent = sTalentStore.LookupEntry(i)) // Someday, someone needs to revamp the way talents are tracked
        {
            if (talentInfo->classId == tmpTalent->classId &&
                talentInfo->column != tmpTalent->column &&
                talentInfo->row == tmpTalent->row)
            {
                if (HasSpell(tmpTalent->spellId))
                    return false;
            }
        }
    }

    // learn! (other talent ranks will unlearned at learning)
    AddTalent(talentInfo, GetActiveSpec(), true);
    learnSpell(spellid, false);
    CastPassiveTalentSpell(spellid);

    TC_LOG_DEBUG("server", "TalentID: %u Spell: %u Spec: %u\n", talentId, spellid, GetActiveSpec());
    return true;
}

