void Player::SendRefundInfo(Item* item)
{
    // This function call unsets ITEM_FLAGS_REFUNDABLE if played time is over 2 hours.
    item->UpdatePlayedTime(this);

    if (!item->HasFlag(ITEM_FIELD_FLAGS, ITEM_FLAG_REFUNDABLE))
    {
        //TC_LOG_DEBUG("player.items", "Item refund: item not refundable!");
        return;
    }

    if (GetGUIDLow() != item->GetRefundRecipient()) // Formerly refundable item got traded
    {
        TC_LOG_DEBUG("player.items", "Item refund: item was traded!");
        item->SetNotRefundable(this);
        return;
    }

    ItemExtendedCostEntry const* iece = sItemExtendedCostStore.LookupEntry(item->GetPaidExtendedCost());
    if (!iece)
    {
        TC_LOG_DEBUG("player.items", "Item refund: cannot find extendedcost data.");
        return;
    }

    ObjectGuid guid = item->GetGUID();
    WorldPacket data(SMSG_ITEM_REFUND_INFO_RESPONSE, 8 + 1 + 5 * 4 + 5 * 4 + 3 * 4);

    data.WriteGuidMask<3, 2, 0, 1, 5, 6, 7, 4>(guid);
    data.WriteGuidBytes<4, 5>(guid);
    data << uint32(0);
    for (uint8 i = 0; i < MAX_ITEM_EXT_COST_ITEMS; ++i)                             // item cost data
    {
        data << uint32(iece->RequiredItem[i]);
        if (iece->RequiredItem[i] == 38186)
            data << uint32(iece->RequiredItemCount[i] * sWorld->getRate(RATE_DONATE));
        else
            data << uint32(iece->RequiredItemCount[i]);
    }
    data.WriteGuidBytes<3>(guid);
    data << uint32(GetTotalPlayedTime() - item->GetPlayedTime());
    data.WriteGuidBytes<7, 0, 1, 2>(guid);
    for (uint8 i = 0; i < MAX_ITEM_EXT_COST_CURRENCIES; ++i)                       // currency cost data
    {
        if (CurrencyTypesEntry const * entry = sCurrencyTypesStore.LookupEntry(iece->RequiredCurrency[i]))
            data << uint32(iece->RequiredCurrencyCount[i] / entry->GetPrecision());
        else
            data << uint32(iece->RequiredCurrencyCount[i]);
        data << uint32(iece->RequiredCurrency[i]);
    }
    data << uint32(item->GetPaidMoney());               // money cost
    data.WriteGuidBytes<6>(guid);

    GetSession()->SendPacket(&data);
}

bool Player::AddItem(uint32 itemId, uint32 count, uint32* noSpaceForCount)
{
    uint32 _noSpaceForCount = 0;
    ItemPosCountVec dest;
    InventoryResult msg = CanStoreNewItem(NULL_BAG, NULL_SLOT, dest, itemId, count, &_noSpaceForCount);
    if (msg != EQUIP_ERR_OK)
        count -= _noSpaceForCount;

    if (noSpaceForCount)
        *noSpaceForCount = _noSpaceForCount;

    if (count == 0 || dest.empty())
    {
        // -- TODO: Send to mailbox if no space
        ChatHandler(this).PSendSysMessage("You don't have any space in your bags.");
        return false;
    }

    if (sObjectMgr->IsPlayerInLogList(this))
    {
        sObjectMgr->DumpDupeConstant(this);
        TC_LOG_DEBUG("dupe", "---AddItem; item: %u; count: %u;", itemId, count);
    }

    Item* item = StoreNewItem(dest, itemId, true, Item::GenerateItemRandomPropertyId(itemId));
    if (item)
        SendNewItem(item, count, true, false);
    else
        return false;
    return true;
}

void Player::SendItemRefundResult(Item* item, ItemExtendedCostEntry const* iece, uint8 error)
{
    ObjectGuid guid = item->GetObjectGuid();
    WorldPacket data(SMSG_ITEM_REFUND_RESULT, 8 + 1 + 1 + 1 + 4 * 5 + 4 * 5 + 4);
    data << uint8(error);
    data.WriteGuidMask<0, 2, 7>(guid);
    data.WriteBit(!error);
    data.WriteGuidMask<5, 3, 4, 6, 1>(guid);
    if (!error)
    {
        for (uint8 i = 0; i < MAX_ITEM_EXT_COST_CURRENCIES; ++i)
        {
            if (iece->IsSeasonCurrencyRequirement(i))
                continue;

            data << uint32(iece->RequiredCurrency[i]);
            if (CurrencyTypesEntry const * entry = sCurrencyTypesStore.LookupEntry(iece->RequiredCurrency[i]))
                data << uint32(iece->RequiredCurrencyCount[i] / entry->GetPrecision());
            else
                data << uint32(iece->RequiredCurrencyCount[i]);
        }
        for (uint8 i = 0; i < MAX_ITEM_EXT_COST_ITEMS; ++i) // item cost data
        {
            data << uint32(iece->RequiredItemCount[i]);
            data << uint32(iece->RequiredItem[i]);
        }
        data << uint32(item->GetPaidMoney());               // money cost
    }

    data.WriteGuidBytes<5, 2, 3, 4, 1, 6, 0, 7>(guid);

    GetSession()->SendPacket(&data);
}

void Player::RefundItem(Item* item, bool saveInDB)
{
    if (!item->HasFlag(ITEM_FIELD_FLAGS, ITEM_FLAG_REFUNDABLE))
    {
        TC_LOG_DEBUG("player.items", "Item refund: item not refundable!");
        return;
    }

    if (item->IsRefundExpired())    // item refund has expired
    {
        item->SetNotRefundable(this);
        SendItemRefundResult(item, NULL, 10);
        return;
    }

    if (GetGUIDLow() != item->GetRefundRecipient()) // Formerly refundable item got traded
    {
        TC_LOG_DEBUG("player.items", "Item refund: item was traded!");
        item->SetNotRefundable(this);
        return;
    }

    ItemExtendedCostEntry const* iece = sItemExtendedCostStore.LookupEntry(item->GetPaidExtendedCost());
    if (!iece)
    {
        TC_LOG_DEBUG("player.items", "Item refund: cannot find extendedcost data.");
        return;
    }

    bool store_error = false;
    for (uint8 i = 0; i < MAX_ITEM_EXT_COST_ITEMS; ++i)
    {
        uint32 count = iece->RequiredItemCount[i];
        uint32 itemid = iece->RequiredItem[i];
        if(itemid == 38186)
            count = uint32(count * sWorld->getRate(RATE_DONATE));

        if (count && itemid)
        {
            ItemPosCountVec dest;
            InventoryResult msg = CanStoreNewItem(NULL_BAG, NULL_SLOT, dest, itemid, count);
            if (msg != EQUIP_ERR_OK)
            {
                store_error = true;
                break;
            }
         }
    }

    if (store_error)
    {
        SendItemRefundResult(item, iece, 10);
        return;
    }

    for (int i = 0; i < MAX_ITEM_EXT_COST_CURRENCIES; ++i)
    {
        if (iece->RequiredCurrency[i] == CURRENCY_NONE)
            continue;

        if (iece->IsSeasonCurrencyRequirement(i))
            continue;

        CurrencyTypesEntry const * entry = sCurrencyTypesStore.LookupEntry(iece->RequiredCurrency[i]);
        if (!entry)
            continue;

        int32 cost = int32(iece->RequiredCurrencyCount[i]);
        TC_LOG_DEBUG("network", "Player::RefundItem  cost %i, id %i)", cost, entry->ID);
        ModifyCurrency(entry->ID, cost, false, true, false, false, true);
    }

    SendItemRefundResult(item, iece, 0);

    uint32 moneyRefund = item->GetPaidMoney();  // item-> will be invalidated in DestroyItem

    // Save all relevant data to DB to prevent desynchronisation exploits
    SQLTransaction trans = CharacterDatabase.BeginTransaction();

    // Delete any references to the refund data
    item->SetNotRefundable(this, true, &trans);

    // Destroy item
    DestroyItem(item->GetBagSlot(), item->GetSlot(), true);

    // Grant back extendedcost items
    for (uint8 i = 0; i < MAX_ITEM_EXT_COST_CURRENCIES; ++i)
    {
        uint32 count = iece->RequiredItemCount[i];
        uint32 itemid = iece->RequiredItem[i];
        if(itemid == 38186)
            count = uint32(count * sWorld->getRate(RATE_DONATE));

        if (count && itemid)
        {
            ItemPosCountVec dest;
            InventoryResult msg = CanStoreNewItem(NULL_BAG, NULL_SLOT, dest, itemid, count);
            ASSERT(msg == EQUIP_ERR_OK); /// Already checked before
            Item* it = StoreNewItem(dest, itemid, true);
            SendNewItem(it, count, true, false, true);
        }
    }

    // Grant back money
    if (moneyRefund)
        ModifyMoney(moneyRefund); // Saved in SaveInventoryAndGoldToDB

    // Grant back Arena and Honor points ?

    if (saveInDB)
        SaveInventoryAndGoldToDB(trans);

    CharacterDatabase.CommitTransaction(trans);
}

