//! 5.4.1
void Player::SendSound(uint32 soundId, ObjectGuid source)
{
    WorldPacket data(SMSG_PLAY_SOUND, 12);
    data.WriteGuidMask<0, 2, 4, 7, 6, 5, 1, 3>(source);
    data.WriteGuidBytes<3, 4, 2, 6, 1, 5, 0>(source);
    data << uint32(soundId);
    data.WriteGuidBytes<7>(source);
    GetSession()->SendPacket(&data);
}

void Player::SendSoundToAll(uint32 soundId, ObjectGuid source)
{
    Map* map = GetMap();
    Map::PlayerList const &_list = map->GetPlayers();
    if (_list.isEmpty())
        return;

    for (Map::PlayerList::const_iterator i = _list.begin(); i != _list.end(); ++i)
    {
        if (Player* player = i->getSource())
            player->SendSound(soundId, source);
    }
}

void Player::_LoadHonor()
{
    // kills in the DB are for one day always
    // so if the last was today then they all were
    QueryResult result = CharacterDatabase.PQuery("SELECT victim_guid,count FROM character_kill WHERE guid='%u'", GetGUIDLow());
    if (result)
    {
        do
        {
            Field *fields  = result->Fetch();
            uint32 victim_guid = fields[0].GetUInt32();
            uint32 count = fields[1].GetUInt32();

            KillInfo &info = m_killsPerPlayer[victim_guid];
            info.state = KILL_UNCHANGED;
            info.count = count;
        }
        while( result->NextRow() );
    }
}

void Player::_SaveHonor()
{
    // the character_kill tables are not used if the config is set to 0
//    if(!sWorld.getConfig(CONFIG_HONOR_KILL_LIMIT))
//        return;

    // flush all kills from the DB at midnight
    if(m_flushKills)
    {
        CharacterDatabase.PExecute("DELETE FROM character_kill WHERE guid = '%u'", GetGUIDLow());
        m_flushKills = false;
    }

    // only if there's something to save
    if(m_saveKills)
    {
        for(KillInfoMap::iterator itr = m_killsPerPlayer.begin(); itr != m_killsPerPlayer.end(); ++itr)
        {
            switch(itr->second.state)
            {
                case KILL_NEW:
                    CharacterDatabase.PExecute("DELETE FROM character_kill WHERE guid = '%u' AND victim_guid = '%u'", GetGUIDLow(), itr->first);
                    CharacterDatabase.PExecute("REPLACE INTO character_kill VALUES ('%u','%u','%u')", GetGUIDLow(), itr->first, itr->second.count);
                    break;
                case KILL_CHANGED:
                    CharacterDatabase.PExecute("UPDATE character_kill SET count='%u' WHERE guid = '%u' AND victim_guid = '%u'", itr->second.count, GetGUIDLow(), itr->first);
                    break;
            }
            itr->second.state = KILL_UNCHANGED;
        }
        m_saveKills = false;
    }
}

void Player::_LoadCustom()
{
    if (QueryResult result = CharacterDatabase.PQuery("SELECT item_guid, enchantId FROM character_visual_enchant WHERE guid = '%u'", GetGUIDLow()))
    {
        do
        {
            Field *fields  = result->Fetch();
            uint32 item_guid = fields[0].GetUInt32();
            uint32 enchantId = fields[1].GetUInt32();

            m_customVisualEnchant[item_guid] = enchantId;
        }
        while( result->NextRow() );
    }
}

uint32 Player::GetEnchantmentVisual(Item* item)
{
    if (!item)
        return 0;

    VisualEnchantMap::const_iterator itr = m_customVisualEnchant.find(item->GetGUIDLow());
    if (itr != m_customVisualEnchant.end())
        return itr->second;

    return item->GetEnchantmentId(PERM_ENCHANTMENT_SLOT);
}


void Player::SendCemeteryList(bool onMap)
{
    ByteBuffer buf(16);
    uint32 count = 0;

    uint32 zoneId = m_zoneUpdateId;
    GraveYardContainer::const_iterator graveLow  = sObjectMgr->GraveYardStore.lower_bound(zoneId);
    GraveYardContainer::const_iterator graveUp   = sObjectMgr->GraveYardStore.upper_bound(zoneId);
    for (GraveYardContainer::const_iterator itr = graveLow; itr != graveUp; ++itr)
    {
        ++count;
        buf << uint32(itr->second.safeLocId);
    }

    //! 5.4.1
    WorldPacket packet(SMSG_REQUEST_CEMETERY_LIST_RESPONSE, buf.wpos()+4);
    packet.WriteBits(count, 22);
    packet.WriteBit(onMap);
    packet.FlushBits();
    packet.append(buf);
    GetSession()->SendPacket(&packet);
}

void Player::SendCategoryCooldownMods()
{
    std::map<uint32, int32> categoryMods;
    Unit::AuraEffectList const& list = GetAuraEffectsByType(SPELL_AURA_MOD_SPELL_CATEGORY_COOLDOWN);
    for (Unit::AuraEffectList::const_iterator itr = list.begin(); itr != list.end(); ++itr)
    {
        std::map<uint32, int32>::iterator cItr = categoryMods.find((*itr)->GetMiscValue());
        if (cItr == categoryMods.end())
            categoryMods[(*itr)->GetMiscValue()] = (*itr)->GetAmount();
        else
            cItr->second += (*itr)->GetAmount();
    }

    //! 5.4.1
    WorldPacket data(SMSG_SPELL_CATEGORY_COOLDOWN, 4 + (int(list.size()) * 8));
    data.WriteBits<int>(list.size(), 21);
    data.FlushBits();
    for (std::map<uint32, int32>::const_iterator itr = categoryMods.begin(); itr != categoryMods.end(); ++itr)
    {
        data << uint32(itr->first);
        data << int32(-itr->second);
    }

    SendDirectMessage(&data);
}

void Player::SendModifyCooldown(uint32 spellId, int32 value)
{
    ObjectGuid guid = GetObjectGuid();
    WorldPacket data(SMSG_MODIFY_COOLDOWN, 4 + 8 + 4 + 1);
    data << int32(value);                   // Cooldown mod in milliseconds
    data << uint32(spellId);                // Spell ID
    data.WriteGuidMask<1, 2, 0, 4, 3, 6, 5, 7>(guid);
    data.WriteGuidBytes<6, 1, 3, 0, 4, 5, 2, 7>(guid);

    SendDirectMessage(&data);
}

