bool Player::CanSpeakLanguage(uint32 lang_id) const
{
    LanguageDesc const* langDesc = GetLanguageDescByID(lang_id);
    if (!langDesc)
        return false;

    if (isGameMaster())
        return true;

    if (langDesc->skill_id != 0 && !HasSkill(langDesc->skill_id))
    {
        // also check SPELL_AURA_COMPREHEND_LANGUAGE (client offers option to speak in that language)
        Unit::AuraEffectList const& langAuras = GetAuraEffectsByType(SPELL_AURA_COMPREHEND_LANGUAGE);
        for (Unit::AuraEffectList::const_iterator i = langAuras.begin(); i != langAuras.end(); ++i)
            if ((*i)->GetMiscValue() == int32(langDesc->lang_id))
                return true;

        return false;
    }

    return true;
}

bool Player::IsForbiddenMapForLevel(uint32 mapid, uint32 zone)
{
    switch (mapid)
    {
        // Eastern Kingdoms
        case 0:
            switch (zone)
            {
                case 4922: // Twilight Highlands
                case 4815: // Vashj'ir: Kelp'thar Forest
                case 5144: // Vashj'ir: Shimmering Expanse
                case 5145: // Vashj'ir: Abyssal Depths
                case 5146: // Vashj'ir
                    if (getLevel() < 80)
                        return true;
                default:
                    break;
            }
            break;
        // Kalimdor
        case 1:
            //      Uldum           Hyjal
            if (zone == 5034 || zone == 616)
                if (getLevel() < 80)
                    return true;
            break;
        // Outland
        case 530:
            // Blood Elf start location and Drenei
            if (!zone || zone == 3430 || zone == 3433 || zone == 3524 || zone == 3525 || zone == 3487 || zone == 3557 || zone == 3479 || zone == 6456 || zone == 6455)
                return false;

            // Isle of Quel'Danas
            if (zone == 4080 && getLevel() < 70) 
                return true;

            if (getLevel() >= 58)
                return false;

            return true;
        // Northrend
        case 571:
            if (getLevel() < 68)
                return true;
            break;
        // Deepholm
        case 646:
            if (getLevel() < 80)
                return true;
            break;        
        case 732:  //Tol Barad
        case 861:  //Molten Front
        case 870:  //Pandaria
        case 1064: //Isle of Giants
            if (getLevel() < 85)
                return true;
            break;
    }

    return false;
}

