void Player::CheckItemCapLevel(bool hasCap)
{
    for (uint8 i = 0; i < EQUIPMENT_SLOT_END; ++i)
    {
        if (m_items[i])
        {
            ItemTemplate const* proto = m_items[i]->GetTemplate();
            if (!proto)
                continue;

            if (proto->ItemLevel <= MIN_ITEM_LEVEL_CUP)
                continue;

            // not need remove
            if (!hasCap && m_items[i]->GetLevel() >= proto->ItemLevel)
                continue;

            if (m_items[i]->IsEquipped() && IsAlive())
            {
                _ApplyItemMods(m_items[i], m_items[i]->GetSlot(), false);
                // on apply perfome all changes
                _ApplyItemMods(m_items[i], m_items[i]->GetSlot(), true);
            }
        }
    }
}

// Just for test. 
// ToDo: add field with corect vignitte id for bosses.
// ToDo2: system should work not at creature udapte, but at zone entering.
void Player::SendVignette(bool force)
{
    if(m_vignettes.empty())
        return;

    uint32 removedCount = 0;
    uint32 addedCount = 0;
    uint32 updatedCount = 0;

    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr)
    {
        if (itr->second.remove)
            removedCount++;
        if (itr->second.add)
            addedCount++;
        if (itr->second.update)
            updatedCount++;
    }

    if(!removedCount && !addedCount && !updatedCount)
        return;

    WorldPacket data(SMSG_CLIENT_VIGNETTE_DATA, 20);
    data.WriteBits(removedCount, 24); // dword34 vignet guid remove
    data.WriteBits(updatedCount, 24); // dword10 vignet guid update

    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword34 vignet guid remove
    {
        if (itr->second.remove)
            data.WriteGuidMask<0, 4, 1, 7, 6, 2, 5, 3>(itr->second.guid);
    }
    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword10 vignet guid update
    {
        if (itr->second.update)
            data.WriteGuidMask<7, 5, 6, 3, 1, 2, 0, 4>(itr->second.guid);
    }

    data.WriteBit(force); // byte30 force
    data.WriteBits(addedCount, 20); // dword54 client data add
    data.WriteBits(updatedCount, 20); // dword20 client data update

    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword20 client data update
    {
        if (itr->second.update)
            data.WriteGuidMask<6, 3, 1, 2, 5, 7, 4, 0>(itr->first);
    }
    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword54 client data add
    {
        if (itr->second.add)
            data.WriteGuidMask<0, 5, 6, 7, 3, 1, 4, 2>(itr->first);
    }

    data.WriteBits(addedCount, 24); // dword44 vignet guid add

    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword44 vignet guid add
    {
        if (itr->second.add)
            data.WriteGuidMask<1, 5, 0, 2, 4, 7, 3, 6>(itr->second.guid);
    }

    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword44 vignet guid add
    {
        if (itr->second.add)
            data.WriteGuidBytes<0, 2, 3, 4, 5, 1, 7, 6>(itr->second.guid);
    }
    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end();) // dword34 vignet guid remove
    {
        if (itr->second.remove)
        {
            data.WriteGuidBytes<7, 4, 1, 0, 5, 2, 3, 6>(itr->second.guid);
            m_vignettes.erase(itr++);
            continue;
        }
        ++itr;
    }
    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword54 client data add
    {
        if (itr->second.add)
        {
            data << float(itr->second.position.GetPositionX());
            data.WriteGuidBytes<2, 4>(itr->first);
            data << float(itr->second.position.GetPositionY());
            data.WriteGuidBytes<6, 7, 0, 3>(itr->first);
            data << itr->second.vignetteId;
            data << float(itr->second.position.GetPositionZ());
            data.WriteGuidBytes<5, 1>(itr->first);
            itr->second.add = false;
        }
    }
    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword10 vignet guid update
    {
        if (itr->second.update)
            data.WriteGuidBytes<4, 1, 0, 5, 7, 2, 6, 3>(itr->second.guid);
    }
    for (auto itr = m_vignettes.begin(); itr != m_vignettes.end(); ++itr) // dword20 client data update
    {
        if (itr->second.update)
        {
            data.WriteGuidBytes<20, 7>(itr->first);
            data << float(itr->second.position.GetPositionX());
            data << float(itr->second.position.GetPositionY());
            data.WriteGuidBytes<5, 4, 1, 2, 6>(itr->first);
            data << itr->second.vignetteId;
            data.WriteGuidBytes<3>(itr->first);
            data << float(itr->second.position.GetPositionZ());
            itr->second.update = false;
        }
    }
    GetSession()->SendPacket(&data);
}

bool TeleportEvent::Execute(uint64, uint32)
{
    if (!m_owner->IsInWorld() || m_owner->IsBeingTeleported())
        return false;

    if (scheduleDelayed)
    {
        m_owner->ScheduleDelayedOperation(DELAYED_BG_MOUNT_RESTORE);
        m_owner->ScheduleDelayedOperation(DELAYED_BG_TAXI_RESTORE);
        m_owner->ScheduleDelayedOperation(DELAYED_BG_GROUP_RESTORE);
    }

    bool ok = m_owner->TeleportTo(mapId, x, y, z, o, params);
    if (lfg)
        m_owner->GetSession()->SendLfgTeleportError(ok ? lfg::LFG_TELEPORTERROR_OK : lfg::LFG_TELEPORTERROR_INVALID_TELEPORT_LOCATION);

    return true;
}

bool TeleportEvent::Schedule()
{
    if (mapId == MAPID_INVALID)
        return false;

    m_owner->m_Events.AddEvent(this, m_owner->m_Events.CalculateTime(m_delay));
    return true;
}

bool Player::GetRPPMProcChance(double &cooldown, float RPPM, const SpellInfo* spellProto, uint64 itemGUID)
{
    if (cooldown)
        return false;

    double preciseTime = getPreciseTime();
    double averageProcInterval = 60.0f / RPPM;
    double timeSinceLastSuccessfulProc = preciseTime - GetLastSuccessfulProc(spellProto->Id, itemGUID);
    double timeSinceLastChanceToProc = preciseTime - GetLastChanceToProc(spellProto->Id, itemGUID);

    if (timeSinceLastChanceToProc > 10.0)
        timeSinceLastChanceToProc = 10.0;

    if (timeSinceLastSuccessfulProc > 1000.0)
        timeSinceLastSuccessfulProc = 1000.0;

    float HasteOrCritMod = 1.0f;

    switch (spellProto->Id)
    {
        case 139134: case 139171:
            HasteOrCritMod += GetFloatValue(PLAYER_CRIT_PERCENTAGE) / 100.0f;
            break;
        case 138849: case 138924:
        case 138968: case 139190:
        case 137595: case 146316:
        case 146197: case 146195:
        case 138367: case 138365:
        case 118314: case 104428:
        case 104441:
            HasteOrCritMod = GetMaxBaseHastRatingPct();
            break;
        default:
            break;
    }

    float multiplier = std::max(1.0f, 1.0f + (float(timeSinceLastSuccessfulProc / averageProcInterval) - 1.5f) * 3.0f);
    float chance = multiplier * RPPM * HasteOrCritMod * (timeSinceLastChanceToProc) / 60.0f * 100.0f;

    cooldown = spellProto->procTimeRec / 1000.0;

    SetLastChanceToProc(spellProto->Id, preciseTime, itemGUID);

    return roll_chance_f(chance);
}

double Player::GetLastSuccessfulProc(uint32 spell_id, uint64 itemGUID)
{
    double lastSuccessfulProc = 0.0;

    for (auto itr : m_rppmspellCooldowns)
        if (itr.spellId == spell_id && itr.itemGUID == itemGUID)
            lastSuccessfulProc = itr.lastSuccessfulProc;

    return lastSuccessfulProc;
}
double Player::GetLastChanceToProc(uint32 spell_id, uint64 itemGUID)
{
    double lastChanceToProc = 0.0;

    for (auto itr : m_rppmspellCooldowns)
        if (itr.spellId == spell_id && itr.itemGUID == itemGUID)
            lastChanceToProc = itr.lastChanceToProc;

    return lastChanceToProc;
}
void Player::SetLastSuccessfulProc(uint32 spell_id, double time, uint64 itemGUID)
{
    for (RPPMSpellCooldowns::iterator itr = m_rppmspellCooldowns.begin(); itr != m_rppmspellCooldowns.end(); ++itr)
        if (itr->spellId == spell_id && itr->itemGUID == itemGUID)
            itr->lastSuccessfulProc = time;
}
void Player::SetLastChanceToProc(uint32 spell_id, double time, uint64 itemGUID)
{
    for (RPPMSpellCooldowns::iterator itr = m_rppmspellCooldowns.begin(); itr != m_rppmspellCooldowns.end(); ++itr)
        if (itr->spellId == spell_id && itr->itemGUID == itemGUID)
            itr->lastChanceToProc = time;
}

void Player::UpdateSpellHastDurationRecovery()
{
    flag128 _mask = 0;
//     for (PlayerSpellMap::iterator itr = m_spells.begin(); itr != m_spells.end(); ++itr)
//     {
//         uint32 SpellId = itr->first;

        if (SpellInfo const* spellInfo = sSpellMgr->GetSpellInfo(/*SpellId*/116847))
//             if (!spellInfo->IsChanneled() && (spellInfo->AttributesEx8 & SPELL_ATTR8_HASTE_AFFECT_DURATION_RECOVERY) && spellInfo->RecoveryTime == spellInfo->GetMaxDuration())
//             {
                _mask |= spellInfo->SpellFamilyFlags;
//             }
//     }

    if (_mask)
    {
        for (SpellModList::iterator itr = m_spellMods[SPELLMOD_COOLDOWN].begin(); itr != m_spellMods[SPELLMOD_COOLDOWN].end(); ++itr)
            if ((*itr)->spellId == 60000)
            {
                m_spellMods[SPELLMOD_COOLDOWN].remove(*itr);
                break;
            }

        SpellModifier* modApply = new SpellModifier();
        modApply->op = SPELLMOD_COOLDOWN;
        modApply->type = SPELLMOD_PCT;
        modApply->spellId = 60000;
        modApply->mask = _mask;
        modApply->value = -(100.0f - (GetFloatValue(UNIT_MOD_CAST_HASTE) * 100.0f));
        AddSpellMod(modApply, true);
    }
}

void Player::HandleArenaDeserter()
{
    AddAura(125761, this);

    if (Aura* deserterAura = GetAura(125761))
    {
        if (Aura* dRAura = GetAura(147303))
        {
            if (AuraEffect* aRAuraEff = dRAura->GetEffect(EFFECT_0))
            {
                switch (RoundingFloatValue(aRAuraEff->GetAmount()))
                {
                    case 0:
                    {
                        deserterAura->SetDuration(300000);
                        deserterAura->SetMaxDuration(300000);
                        aRAuraEff->SetAmount(aRAuraEff->GetAmount() + 1);
                        break;
                    }
                    case 1:
                    {
                        deserterAura->SetDuration(900000);
                        deserterAura->SetMaxDuration(900000);
                        aRAuraEff->SetAmount(aRAuraEff->GetAmount() + 1);
                        break;
                    }
                    default:
                    {
                        deserterAura->SetDuration(1800000);
                        deserterAura->SetMaxDuration(1800000);
                        break;
                    }
                }
                dRAura->RefreshTimers();
            }
        }
        else
            AddAura(147303, this);
    }
}

SpellModifier* Player::TryFindMod(SpellModType type, SpellModOp Op, uint32 spellId, int32 val)
{
    for (auto itr : m_spellMods[Op])
    {
        SpellModifier* mod = itr;

        if (mod->type == type && mod->spellId == spellId && mod->value == val)
            return mod;
    }

    return NULL;
}

void Player::AddListner(WorldObject* o, bool /*update*/)
{
    if(CanSeeVignette(o))
        AddVignette(o);
}

void Player::RemoveListner(WorldObject* o, bool update)
{
    if(o->GetVignetteId())
    {
        RemoveVignette(o);
        if(update)
            SendVignette();
    }
}

bool Player::CanSeeVignette(WorldObject *o)
{
    if(!o->GetVignetteId() || (o->ToUnit() && (!o->ToUnit()->IsAlive() || o->ToUnit()->IsPet())))
        return false;

    /*if (Creature const* creature = o->ToCreature())
    {
        uint32 questId = creature->GetCreatureTemplate()->personalloot;
        if(questId && GetQuestStatus(questId) == QUEST_STATUS_COMPLETE)
            return false;
    }*/
    return true;
}

void Player::AddVignette(WorldObject *o)
{
    PlayerVignette vignette;
    vignette.guid = o->GetVignetteGUID();
    vignette.vignetteId = o->GetVignetteId();
    vignette.zoneId = o->GetCurrentZoneId();
    vignette.add = true;
    vignette.remove = false;
    vignette.update = false;
    vignette.position.Relocate(o);
    m_vignettes.insert(PlayerVignettesMap::value_type(o->GetGUID(), vignette));
}

void Player::RemoveVignette(WorldObject *o, bool update)
{
    PlayerVignettesMap::iterator itr = m_vignettes.find(o->GetGUID());
    if (itr == m_vignettes.end())
        return;

    itr->second.remove = true;
    if(update)
        SendVignette();
}

void Player::SendEncounterUnitForPlayer(uint32 type, Unit* unit /*= NULL*/, uint8 param1 /*= 0*/, uint8 param2 /*= 0*/)
{
    // size of this packet is at most 15 (usually less)
    WorldPacket data(SMSG_UPDATE_INSTANCE_ENCOUNTER_UNIT, 15);
    data << uint32(type);

    switch (type)
    {
    case ENCOUNTER_FRAME_ENGAGE:
    case ENCOUNTER_FRAME_DISENGAGE:
    case ENCOUNTER_FRAME_UPDATE_PRIORITY:
        if (!unit)
            return;
        data.append(unit->GetPackGUID());
        data << uint8(param1);
        break;
    case ENCOUNTER_FRAME_ADD_TIMER:
    case ENCOUNTER_FRAME_ENABLE_OBJECTIVE:
    case ENCOUNTER_FRAME_DISABLE_OBJECTIVE:
    case ENCOUNTER_FRAME_SET_COMBAT_RES_LIMIT:
        data << uint8(param1);
        break;
    case ENCOUNTER_FRAME_UPDATE_OBJECTIVE:
        data << uint8(param1);
        data << uint8(param2);
        break;
    case ENCOUNTER_FRAME_UNK7:
    case ENCOUNTER_FRAME_ADD_COMBAT_RES_LIMIT:
    case ENCOUNTER_FRAME_RESET_COMBAT_RES_LIMIT:
    default:
        break;
    }
    GetSession()->SendPacket(&data);
}

SpellModList Player::GetModAndSpellMod(SpellModOp op, uint32 spellId)
{
    SpellModList tempModList = m_spellMods[op];

    if (Spell* _spell = FindCurrentSpellBySpellId(spellId))
        if (!_spell->m_spellModList.empty())
        {
            for (auto itr : _spell->m_spellModList)
                if (SpellModifier* mod = itr)
                {
                    if (mod->op != op)
                        continue;

                    bool findSimilar = false;

                    for (auto itr2 : m_spellMods[op])
                        if (SpellModifier* mod2 = itr2)
                        {
                            if (mod->spellId == mod2->spellId && mod->type == mod2->type && mod->value == mod2->value)
                            {
                                findSimilar = true;
                                break;
                            }
                        }

                    if (!findSimilar)
                        tempModList.push_back(mod);
                }
        }

    return tempModList;
}

SpellModifier* Player::ChangePriorityMods(SpellModifier* currentMod, SpellModifier* newMod, SpellInfo const* spellInfo)
{
    uint8 currentModVal = 0;
    uint8 newModVal = 0;

    if (SpellInfo const* curInf = sSpellMgr->GetSpellInfo(currentMod->spellId))
        for (uint8 i = 0; i < MAX_SPELL_EFFECTS; i++)
            if (curInf->Effects[i]->Effect == SPELL_EFFECT_APPLY_AURA)
                switch (curInf->Effects[i]->ApplyAuraName)
                {
                    case SPELL_AURA_ADD_PCT_MODIFIER:
                    case SPELL_AURA_ADD_FLAT_MODIFIER:
                    {
                        if (IsAffectedBySpellmod(spellInfo, currentMod))
                            currentModVal++;
                        continue;
                    }
                    default:
                        continue;
                }

    if (SpellInfo const* newInf = sSpellMgr->GetSpellInfo(newMod->spellId))
        for (uint8 i = 0; i < MAX_SPELL_EFFECTS; i++)
            if (newInf->Effects[i]->Effect == SPELL_EFFECT_APPLY_AURA)
                switch (newInf->Effects[i]->ApplyAuraName)
                {
                    case SPELL_AURA_ADD_PCT_MODIFIER:
                    case SPELL_AURA_ADD_FLAT_MODIFIER:
                    {
                        if (IsAffectedBySpellmod(spellInfo, newMod))
                            newModVal++;
                        continue;
                    }
                    default:
                        continue;
                }

    if (newModVal > currentModVal)
        return newMod;

    return currentMod;
}

