UnitMoveType Player::GetMovementType(uint32 moveflags)
{
    if (moveflags & MOVEMENTFLAG_SWIMMING)
    {
        if (moveflags & MOVEMENTFLAG_BACKWARD)
            return MOVE_SWIM_BACK;
        else
            return MOVE_SWIM;
    }
    else if (moveflags & (MOVEMENTFLAG_CAN_FLY | MOVEMENTFLAG_FLYING))
    {
        if (moveflags & MOVEMENTFLAG_BACKWARD)
            return MOVE_FLIGHT_BACK;
        else
            return MOVE_FLIGHT;
    }
    else if (moveflags & MOVEMENTFLAG_WALKING)
        return MOVE_WALK;
    else if (moveflags & MOVEMENTFLAG_BACKWARD)
        return MOVE_RUN_BACK;

    return MOVE_RUN;
}

uint32 Player::LoadControlableGuardianActionBarInfo(uint32 entry)
{
    if (!m_CGActionBarInfo.empty())
    {
        std::map<uint32, uint16>::iterator itr = m_CGActionBarInfo.find(entry);
        if (itr != m_CGActionBarInfo.end())
            return itr->second;
    }

    uint32 infoMask = 0;

    PreparedStatement* stmt = CharacterDatabase.GetPreparedStatement(CHAR_SEL_CG_ACTION_BAR_INFO);
    stmt->setUInt64(0, GetGUID());
    PreparedQueryResult result = CharacterDatabase.Query(stmt);
    
    if (result)
    {
        do
        {
            Field* fields = result->Fetch();

            uint32 _entry = fields[0].GetUInt32();
            uint16 _infoMask = fields[1].GetUInt16();

            m_CGActionBarInfo[_entry] = _infoMask;

            if (_entry == entry)
                infoMask = _infoMask;

        } while (result->NextRow());
    }

    return infoMask;
}

void Player::SaveControlableGuardianActionBarInfo(uint32 entry, uint16 infoMask)
{
    SQLTransaction trans = CharacterDatabase.BeginTransaction();
    PreparedStatement* stmt;
    uint64 guid = GetGUID();

    stmt = CharacterDatabase.GetPreparedStatement(CHAR_DEL_CG_ACTION_BAR_INFO);
    stmt->setUInt64(0, guid);
    stmt->setUInt32(1, entry);
    trans->Append(stmt);

    stmt = CharacterDatabase.GetPreparedStatement(CHAR_INS_CG_ACTION_BAR_INFO);
    stmt->setUInt64(0, guid);
    stmt->setUInt32(1, entry);
    stmt->setUInt16(2, infoMask);
    trans->Append(stmt);

    CharacterDatabase.CommitTransaction(trans);
    m_CGActionBarInfo[entry] = infoMask;
}

void Player::HandleSellItemOpcode()
{
    for (uint8 i = 0; i < MAX_SELL_ITEMS_IN_QUEUE; i++)
    {
        uint64 itemguid = m_sellItemList[i].itemGUID;

        if (!itemguid)
            return;

        uint64 vendorguid = m_sellItemList[i].vendorguid;
        uint32 count = m_sellItemList[i].count;

        TC_LOG_DEBUG("network", "WORLD: Received CMSG_SELL_ITEM");

        if (!itemguid)
        {
            TC_LOG_DEBUG("network", "WORLD: HandleSellItemOpcode - itemguid not fount.");
            continue;
        }

        Creature* creature = GetNPCIfCanInteractWith(vendorguid, UNIT_NPC_FLAG_VENDOR);
        if (!creature)
        {
            TC_LOG_DEBUG("network", "WORLD: HandleSellItemOpcode - Unit (GUID: %u) not found or you can not interact with him.", uint32(GUID_LOPART(vendorguid)));
            SendSellError(SELL_ERR_VENDOR_HATES_YOU, NULL, itemguid);
            continue;
        }

        if (sObjectMgr->IsPlayerInLogList(this))
        {
            sObjectMgr->DumpDupeConstant(this);
            TC_LOG_DEBUG("dupe", "---HandleSellItemOpcode; vendor: %llu; item: %u; count %u", GUID_LOPART(vendorguid), GUID_LOPART(itemguid), count);
        }

        // remove fake death
        if (HasUnitState(UNIT_STATE_DIED))
            RemoveAurasByType(SPELL_AURA_FEIGN_DEATH);

        Item* pItem = GetItemByGuid(itemguid);
        if (pItem)
        {
            // prevent sell not owner item
            if (GetGUID() != pItem->GetOwnerGUID())
            {
                SendSellError(SELL_ERR_CANT_SELL_ITEM, creature, itemguid);
                continue;
            }

            // prevent sell non empty bag by drag-and-drop at vendor's item list
            if (pItem->IsNotEmptyBag())
            {
                SendSellError(SELL_ERR_CANT_SELL_ITEM, creature, itemguid);
                continue;
            }

            // prevent sell currently looted item
            if (GetLootGUID() == pItem->GetGUID())
            {
                SendSellError(SELL_ERR_CANT_SELL_ITEM, creature, itemguid);
                continue;
            }

            // prevent selling item for sellprice when the item is still refundable
            // this probably happens when right clicking a refundable item, the client sends both
            // CMSG_SELL_ITEM and CMSG_REFUND_ITEM (unverified)
            if (pItem->HasFlag(ITEM_FIELD_FLAGS, ITEM_FLAG_REFUNDABLE))
            {
                RefundItem(pItem, false);
                continue; // Therefore, no feedback to client
            }

            if (pItem->GetEntry() == 38186)
                TC_LOG_DEBUG("efir", "HandleBuyItemInSlotOpcode item %u; count = %u playerGUID %u vendorguid %u", pItem->GetEntry(), count, GetGUID(), creature->GetGUID());

            // special case at auto sell (sell all)
            if (count == 0)
                count = pItem->GetCount();
            else
            {
                // prevent sell more items that exist in stack (possible only not from client)
                if (count > pItem->GetCount())
                {
                    SendSellError(SELL_ERR_CANT_SELL_ITEM, creature, itemguid);
                    continue;
                }
            }

            ItemTemplate const* pProto = pItem->GetTemplate();
            if (pProto)
            {
                if (pProto->SellPrice > 0)
                {
                    if (count < pItem->GetCount())               // need split items
                    {
                        Item* pNewItem = pItem->CloneItem(count, this);
                        if (!pNewItem)
                        {
                            TC_LOG_ERROR("network", "WORLD: HandleSellItemOpcode - could not create clone of item %u; count = %u", pItem->GetEntry(), count);
                            SendSellError(SELL_ERR_CANT_SELL_ITEM, creature, itemguid);
                            continue;
                        }
                        if (pItem->GetEntry() == 38186)
                            TC_LOG_DEBUG("efir", "HandleSellItemOpcode - CloneItem of item %u; count = %u playerGUID %u, itemGUID %u", pItem->GetEntry(), count, GetGUID(), pItem->GetGUID());

                        pItem->SetCount(pItem->GetCount() - count);
                        ItemRemovedQuestCheck(pItem->GetEntry(), count);
                        if (IsInWorld())
                            pItem->SendUpdateToPlayer(this);
                        pItem->SetState(ITEM_CHANGED, this);

                        AddItemToBuyBackSlot(pNewItem);
                        if (IsInWorld())
                            pNewItem->SendUpdateToPlayer(this);
                    }
                    else
                    {
                        ItemRemovedQuestCheck(pItem->GetEntry(), pItem->GetCount());
                        RemoveItem(pItem->GetBagSlot(), pItem->GetSlot(), true);
                        pItem->RemoveFromUpdateQueueOf(this);
                        AddItemToBuyBackSlot(pItem);
                    }

                    uint32 money = pProto->SellPrice * count;
                    ModifyMoney(money);
                    UpdateAchievementCriteria(CRITERIA_TYPE_MONEY_FROM_VENDORS, money);
                    SendSellError(SELL_ERR_OK, creature, itemguid);
                }
                else
                    SendSellError(SELL_ERR_CANT_SELL_ITEM, creature, itemguid);
                continue;
            }
        }
        SendSellError(SELL_ERR_CANT_FIND_ITEM, creature, itemguid);
    }
}

bool Player::AddToQueueSellItemOpcode(sellItemOpcodeInfo info)
{
    for (uint8 i = 0; i < MAX_SELL_ITEMS_IN_QUEUE; i++)
        if (!m_sellItemList[i].itemGUID)
        {
            m_sellItemList[i] = info;

            if (i == 5) 
                m_sellItemTimer = 0;

            return true;
        }

    return false;
}

void Player::ClearSellItemList()
{
    for (uint8 i = 0; i < MAX_SELL_ITEMS_IN_QUEUE; i++)
    {
        m_sellItemList[i].count = 0;
        m_sellItemList[i].itemGUID = 0;
        m_sellItemList[i].vendorguid = 0;
    }
}

void Player::HandleBuyItemOpcode()
{
    std::vector<buyItemOpcodeInfo> tempList = m_buyItems;
    m_buyItems.clear();

    for (auto itr : tempList)
    {
        uint64 bagGuid = itr.bagGuid;
        uint64 vendorguid = itr.vendorguid;
        uint32 count = itr.count;
        uint32 item = itr.item;
        uint32 slot = itr.slot;
        uint32 bagSlot = itr.bagSlot;
        uint8 itemType = itr.itemType; // 1 = item, 2 = currency

        if (itemType == ITEM_VENDOR_TYPE_ITEM)
        {
            uint8 bag = NULL_BAG;

            if (bagGuid == GetObjectGuid())
                bag = INVENTORY_SLOT_BAG_0;
            else
            {
                Item* bagItem = GetItemByGuid(bagGuid);
                if (bagItem && bagItem->IsBag())
                    bag = bagItem->GetSlot();
            }

            BuyItemFromVendorSlot(vendorguid, slot, item, count, bag, bagSlot);
        }
        else if (itemType == ITEM_VENDOR_TYPE_CURRENCY)
            BuyCurrencyFromVendorSlot(vendorguid, slot, item, count);
        else
            TC_LOG_DEBUG("network", "WORLD: received wrong itemType (%u) in HandleBuyItemOpcode", itemType);
    }
}

void Player::SendVersionMismatchWarinings()
{
    char buff[2048];
    sprintf(buff, GetSession()->GetTrinityString(LANG_CLIENT_VERSION_MISMATCH_MESSAGE));
    sWorld->SendServerMessage(SERVER_MSG_STRING, buff);

    GetSession()->SendNotification(LANG_CLIENT_VERSION_MISMATCH_MESSAGE_NOTIFY);
}